import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCocktail } from '@fortawesome/free-solid-svg-icons';

const exampleData = [
  {
    name: "test",
    date: "02/02/21",
    creator: "Darth Vader",
    ingredients: {},
    instructions: "Shake for 10 seconds.",
    description: "very good",
  },
  {
    name: "south side",
    date: "02/02/21",
    creator: "Jar Jar",
    ingredients: {},
    instructions: "Shake for 10 seconds.",
    description: "very good",
  },
]

// The individual cocktail object component
function Cocktail({cocktail}) {
  return (
    <div>
      <FontAwesomeIcon icon={faCocktail} />
      <div>{cocktail.name}</div>
    </div>
  );
}

// The list of Cocktail Components
function CocktailList({ data }) {
  return (
    <div className="flex pa3 ma3 ba">
      {
        data.map(cocktail => {
          return <Cocktail cocktail={cocktail} />
        })
      }
    </div>
  );
}

function App() {
  return (
    <div className="tc center">
      <h1>Cocktail App</h1>
      <div>
        <CocktailList data={exampleData} />
      </div>
    </div>
  );
}

export default App;
