import React from 'react';
import Help from './Help';
import helpText from '../static/helpText';
import {
  Box,
  Drawer,
  Typography,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 10,
  },
  helpContainer: {
    zIndex: 2,
    minHeight: '40vh',
    position: 'fixed',
    padding: 20,
  },
}));

export default function InfoDrawer({ step, isOpen, handleDrawer }) {
  const classes = useStyles();

  const steps = () => {
    switch (step) {
      case 0:
        return helpText[0]
      case 1:
        return helpText[1]
      case 2:
        return helpText[2]
      case 3:
        return helpText[3]
      default:
        return false
    }
  }

  return (
    <Drawer
      anchor='right'
      open={isOpen}
      onClose={handleDrawer}
      variant='temporary'
    >
      <div className={classes.root}>
      {
        step <= 3
        ? <Box 
            display="flex"
            flexWrap="wrap"
            border={1} 
            boxShadow={1}
            borderColor="primary.main" 
            bgcolor="#505050"
            borderRadius={37}
            className={classes.helpContainer}
          >
            <Typography 
              component="p" 
              color="textSecondary"
            >
              { steps() }
            </Typography>
          </Box>
        : false
      }
      </div>
    </Drawer>
  );
}
