import React, { useState } from 'react';
import MetricSelector from '../Components/MetricSelector';
import ImperialSelector from '../Components/ImperialSelector';
import spirits from '../static/spirits';
import {
  Container,
  TextField,
  FormControl,
  FormControlLabel,
  Select,
  InputLabel,
  Checkbox,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  input: {
    width: 650
  },
}));

export default function BaseSpirit() {
  const classes = useStyles();
  const [isMetric, setIsMetric] = useState(false);

  const handleIsMetric = () => {
    setIsMetric(!isMetric)
  }

  return (
    <Container maxWidth="lg" className={classes.formContainer}>
      <FormControlLabel
        control={<Checkbox color="secondary" id="metric" size="small" />}
        label="Metric"
        onChange={handleIsMetric}
      />
      <div>
        <TextField 
          id='ingredient'
          label='Ingredient'
          variant='outlined'
          margin='normal'
          InputProps={{
            className: classes.input
          }}
        />
    <FormControl className={classes.formControl}>
              <InputLabel htmlFor="search-filter">Filter by</InputLabel>
              <Select defaultValue="" id="search-filter">
                <option value={'name'} aria-label='Cocktail Name'>Cocktail Name</option>
                <option value={'creator'} aria-label='Cocktail Creator'>Cocktail Creator</option>
                <optgroup label='Base Spirit'>
                {
                  spirits.map((spirit, id) => {
                    return <option value={spirit} aria-label={spirit}>{spirit}</option>
                  })
                }
                </optgroup>
              </Select>
            </FormControl>
      </div>
      {
        isMetric
          ? <MetricSelector />
          : <ImperialSelector />
      }
    </Container>
  );
}
