import React, { useState, useEffect } from 'react';
import MainBar from '../Helpers/MainBar';
import Help from './Help';
import CocktailStepper from './CocktailStepper';
import RecipeStepper from './RecipeStepper';
import {
  Container,
  Grid,
  Typography,
  Collapse,
  IconButton,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  FormControl,
  FormLabel,
  FormControlLabel,
  RadioGroup,
  Radio,
  Button,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import InfoOutlinedIcon from '@material-ui/icons/InfoOutlined';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  appBar: {
    backgroundColor: '#303030',
    position: 'sticky',
    top: 0,
    zIndex: 1,
  },
  infoButton: {
    float: 'right',
  },
  button: {
  borderRadius: 37,
    width: 250
  },
  buttonDiv: {
    flex: 1,
    margin: 25,
  textAlign: 'center',
  },
}));

export default function Wizard() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = useState(0);
  const [showHelp, setShowHelp] = useState(false);
  const [readyForReview, setReadyForReview] = useState(false);
  const [recipeType, setRecipeType] = useState(false);
  const [showRecipePrompt, setShowRecipePrompt] = useState(true);

  const handleHelp = () => {
    setShowHelp(!showHelp)
  }

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1)
  }

  const handleNext = () => {
    // This should also be pushing the entered data into the object
    // This could get complicated. I'll need to give each input a unique ID so that if I backtrack I can have it update
    // The unique item you'd want to change otherwise stuff could get messy
    if (activeStep < steps.length) {
      setActiveStep((prevActiveStep) => prevActiveStep + 1)
    } 
  }

  const handleRecipeType = (event) => {
    setRecipeType(event.target.value)
    setShowRecipePrompt(false)
  }

  const handleReview = () => {
    setReadyForReview(true)
  }

  return (
    <main>
      { 
        // Show help text if the InfoOutlinedIcon is clicked
        //showHelp ? <Help step={activeStep} /> : false 
      }
      <MainBar />
      <div>
      {
        showRecipePrompt ? 
        <Container className={classes.formContainer}>
          <Grid container alignItems="center">
            <Grid item xs>
            <Typography>
              What kind of recipe is this?
            </Typography>
            </Grid>
            <Grid item xs>
              <FormControl component="fieldset">
                <FormLabel component="legend">Recipe Type</FormLabel>
                <RadioGroup aria-label="recipe-type" name="recipe-type" onChange={handleRecipeType}>
                  <FormControlLabel value="cocktail" control={<Radio />} label="Cocktail" />
                  <FormControlLabel value="msi" control={<Radio />} label="Mixer/Syrup/Infusion" />
                  <FormControlLabel value="other" control={<Radio />} label="Other" />
                </RadioGroup>
              </FormControl>
            </Grid>
          </Grid>
          <Button
            variant="outlined"
            color="primary"
            className={classes.button}
          >
            Next
          </Button>
        </Container>
        : 
        <div className={classes.appBar}>
          <IconButton disabled={activeStep === 0} onClick={handleBack}>
            <ExpandLessIcon />
          </IconButton>
          <Scroll to={`step-${activeStep}`} smooth="true">
            <IconButton
              variant="contained"
              color="primary"
              onClick={handleNext}
            >
              {
                /*
                 * If you're on the last step then display the CheckCircle instead of the ExpandMore
                 */
                activeStep >= steps.length - 1
                  ? <CheckCircleIcon onClick={handleReview} />
                  : <ExpandMoreIcon />
              }
            </IconButton>
          </Scroll>
          {
            /*
             * If you're at the end of your stepper then stop showing the help icon
             */
            activeStep <= steps.length - 1
              ? <IconButton className={classes.infoButton} onClick={handleHelp}>
                  <InfoOutlinedIcon />
                </IconButton>
              : false
          }
        </div>
      }
      </div>
      <div>
      {
        /*
         * Decide what Stepper type to show
         */
        !recipeType ? false : (
          recipeType === 'cocktail'
            ? <CocktailStepper activeStep={activeStep} />
            : <RecipeStepper activeStep={activeStep} />
        )
      }
      </div>
      <div className={classes.buttonDiv}>
      {
        /*
         * Once all steps are completed then display the Review button
         */
        readyForReview && activeStep >= steps.length
          ? <Button
              className={classes.button}
              variant="outlined"
              color="primary"
              href="/cocktail"
              onClick={() => console.log("Went to next")}
            >
              Review
            </Button>
          : false
      }
      </div>
    </main>
  );
}
