import React, { useState } from 'react';
import MainBar from '../Helpers/MainBar';
import {
  Container,
  Typography,
  TextField,
  Button,
  Stepper,
  Step,
  StepLabel,
  StepContent,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    marginTop: theme.spacing(6),
    textAlign: 'center',
  },
  textField: {
    borderRadius: 37,
  },
  submitButton: {
    color: 'white',
    marginTop: theme.spacing(1),
    fontWeight: 'bold',
    borderRadius: 50,
    backgroundColor: theme.palette.primary.main,
    '&:hover': {
      backgroundColor: theme.palette.primary.main,
    },
  },
}))

const steps = ["Enter your email", "Reset password", "Enter code"];

function renderStep(step, props) {
  switch(step) {
    case 0:
      return <EnterEmail props={props} />
    case 1:
      return <TextCode props={props} />
    case 2:
      return <Verify props={props} />
    default:
      return false
  }
}

function EnterEmail({ props }) {
  const classes = useStyles();

  return (
    <Container className={classes.root} component="main" maxWidth="sm">
      <Typography variant="subtitle2" color="textSecondary">
        Enter the email associated with your account.
      </Typography>
      <TextField
        id="email"
        label="Email"
        margin="normal"
        required
        fullWidth
        variant="outlined"
        InputProps={{
          className: classes.textField
        }}
      />
      <Button 
        className={classes.submitButton}
        type="submit"
        color="primary"
        fullWidth
        variant="contained"
        onClick={props}
      >
        Search
      </Button>
    </Container>
  );
}

function TextCode({ props }) {
  const classes = useStyles();

  return (
    <Container className={classes.root} component="main" maxWidth="sm">
      <Typography variant="subtitle2" color="textSecondary">
        How would you like to receive your code?
      </Typography>
      <TextField
        id="phone"
        label="Phone"
        margin="normal"
        required
        fullWidth
        variant="outlined"
        InputProps={{
          className: classes.textField
        }}
      />
      <Button 
        className={classes.submitButton}
        type="submit"
        color="primary"
        fullWidth
        variant="contained"
        onClick={props}
      >
        Next
      </Button>
    </Container>
  );
}

function Verify({ props }) {
  const classes = useStyles();

  return (
    <Container className={classes.root} component="main" maxWidth="sm">
      <Typography variant="subtitle2" color="textSecondary">
        Enter the code you received
      </Typography>
      <TextField
        id="code"
        label="Code"
        margin="normal"
        required
        fullWidth
        variant="outlined"
        InputProps={{
          className: classes.textField
        }}
      />
      <Button 
        className={classes.submitButton}
        type="submit"
        color="primary"
        fullWidth
        variant="contained"
        onClick={props}
      >
        Submit
      </Button>
    </Container>
  );
}

export default function ForgotPassword() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = useState(0);

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1)
  }

  const handleNext = () => {
    if (activeStep < steps.length) {
      setActiveStep((prevActiveStep) => prevActiveStep + 1)
    } 
  }

  return (
    <div>
    <MainBar />
    <Stepper activeStep={activeStep} orientation="vertical">
      {
        steps.map((label, index) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
            <StepContent>
              <Typography>{renderStep(index, handleNext)}</Typography>
            </StepContent>
          </Step>
        ))
      }
    </Stepper>
    </div>
  );
}
