import React, { useState } from 'react';
import {
  Typography,
  TextField,
  Button,
  IconButton,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import AddBoxIcon from '@material-ui/icons/AddBox';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
    textAlign: 'center',
  },
}));

export default function Miscellaneous() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const miscArray = [];
  const [misc, setMisc] = useState({});
  const [inputs, setInputs] = useState([{}]);

  const addNewInput = () => setInputs([...inputs, {}])

  const onChangeInput = (e) => {
    setMisc({...misc, [e.target.name]: e.target.value});
  }

  const onSubmitForm = (event) => {
    event.preventDefault()

    cocktail
  }

  return (
    <form className={classes.formContainer} onSubmit={onSubmitForm}>
      <Typography>
        Are there any dry or miscellaneous ingredients?
      </Typography>
      <IconButton onClick={addNewInput} color="primary">
        <AddBoxIcon fontSize="large"/>
      </IconButton>
      {
        inputs.map((input, id) => {
          return (
            <TextField 
              key={`ingredient-${id}`}
              name='name'
              label='Ingredient'
              variant='outlined'
              margin='normal'
              fullWidth
              onChange={(e) => onChangeInput(e)}
            />
          );
        })
      }
    </form>
  );
}
