import React from 'react';
import {
  Container,
  IconButton,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  header: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    backgroundImage: `url(${process.env.PUBLIC_URL + '/assets/cocktail.jpg)'}`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
  },
  info: {
    display: 'flex',
    minHeight: '100vh',
  },
  scrollButton: {
    color: '#ff4081',
    fontSize: '4em',
  },
}));

export default function ItemSheet() {
  const classes = useStyles();

  return (
    <div>
      <div className={classes.header}>
        <Scroll to='drink-info' smooth='true'>
          <IconButton>
            <ExpandMoreIcon className={classes.scrollButton} />
          </IconButton>
        </Scroll>
      </div>
      <Container id="drink-info" className={classes.info}>
        <p> Drink information</p>
      </Container>
    </div>
  );
}
