import React, { useState, useEffect } from 'react';
import MainBar from './MainBar';
import {
  Box,
  Typography,
  Collapse,
  IconButton,
  Container,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  info: {
    minHeight: '100vh',    
    backgroundSize: 'cover',
    backgroundImage: `url(${process.env.PUBLIC_URL + '/assets/dark-cocktail.jpg'})`,
    backgroundPosition: 'center',
    textAlign: 'center',
  },
  appBar: {
    position: 'sticky',
    top: 0,    
    zIndex: 1,
  },
  formDesc: {
    display: 'flex',
    fontFamily: 'Nunito',
    textAlign: 'center',
    justifyContent: 'center',
    backgroundColor: 'rgba(0,0,0,0.3)',
    borderRadius: 37,
    minHeight: 200,
    padding: 10,
  },
  goDown: {
    fontSize: '4rem',    
  },
  createForm: {
    minHeight: '100vh',
  },
}));

export default function Create() {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);
  useEffect(() => {
    setChecked(true);
  });

  return (
    <main className={classes.info}>
      <MainBar />
      <Collapse appear in={checked} {
        ... checked ? { timeout: 2000 } : {}
      }>
        <Box mt={10} m={10}>
          <Typography className={classes.formDesc} color="textSecondary" component="p">
            Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum
          </Typography>
        </Box>
        <Scroll to="createForm" smooth="true">
          <IconButton>
            <ExpandMoreIcon className={classes.goDown} />
          </IconButton>
        </Scroll>
      </Collapse>
      <div id="createForm" className={classes.createForm}>
      </div>
    </main>
  );
}
