import React from 'react';
import MainBar from './MainBar';
import {
	Grid,
	Avatar,
	Container,
	Typography,
} from '@material-ui/core';
import ProfilePic from './assets/dj-pct.jpg';
import QRCode from './assets/test.png';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
	container: {
		display: 'flex',
		margin: 25,
		padding: 20,
		borderRadius: 25,
		backgroundColor: theme.palette.secondary.main,
	},
	profileMain: {
		display: 'flex',
		flexDirection: 'column',
	},
	info: {
		display: 'flex',
		flexDirection: 'column',
		fontFamily: 'Nunito',
		margin: 25
	},
	bio: {
		color: theme.palette.primary.main,
	},
	qrCode: {
		backgroundColor: theme.palette.primary.main,
	},
	editProfile: {
		display: 'flex',
		flexDirection: 'column',
		fontFamily: 'Nunito',
	},
	profilePic: {
		height: theme.spacing(26),
		width: theme.spacing(26),
	},
}));

function QRCodeImage() {
	const classes = useStyles();
	return (
		<div className={classes.qrCode}>
			<img 
				style={{
					height: 200,
					width: 200,
					padding: 5
				}}
				src={QRCode} 
				alt="QRCode" 
			/>
		</div>
	);
}

function EditProfile({ profileData }) {
	const classes = useStyles();

	return (
		<Container>
			<Grid container className={classes.editProfile}>
				<Grid item>
					Name: {profileData.name}
				</Grid>
				<Grid item>
					Occuption: {profileData.occupation}
				</Grid>
				<Grid item>
					Employer: {profileData.employer}
				</Grid>
				<Grid item className={classes.bio}>
					{profileData.bio}
				</Grid>
			</Grid>
		</Container>
	);
}

export default function Profile({ profileData }) {
	const classes = useStyles();

	return (
		<main>
			<div className={classes.container}>
				<Avatar className={classes.profilePic} src={ProfilePic} />
				<Container className={classes.profileMain}>
					<Typography component="h1" variant="h3" className={classes.info}>
						{profileData.name}
					</Typography>
					<QRCodeImage />
				</Container>
			</div>
			<EditProfile profileData={profileData} />
		</main>
	);
}
