import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { 
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography
} from '@material-ui/core';

const useStyles = makeStyles({
  root: {
    maxWidth: 645,
    background: 'rgba(0,0,0,0.5)',
    margin: '20px',
  },
  media: {
    height: 440,
  },
  title: {
    fontFamily: 'Nunito',
    fontWeight: 'bold',
    fontSize: '2rem',
    color: '#fff',
  },
  paragraph: {
    fontFamily: 'Nunito',
    fontsize: '1.1rem',
    color: '#ddd',
  },
});

// So here's how we get an animation to run upon scrolling
// WE don't want those to appear until we scroll
export default function ImageCard({ info, isAnimated}) {
  const classes = useStyles();

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={info.image}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography 
            gutterBottom 
            variant="h5" 
            component="h1" 
            className={classes.title}
          >
            Path No. 1
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p" className={classes.paragraph}>
            {info.desc}
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  );
}
