import React, { useState, useEffect } from 'react';
import MainBar from './MainBar';
import {
  Box,
  Typography,
  Collapse,
  IconButton,
  Container,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  info: {
    minHeight: '100vh',    
    backgroundSize: 'cover',
    backgroundImage: `url(${process.env.PUBLIC_URL + '/assets/cocktail1.jpg'})`,
    backgroundPosition: 'center',
    textAlign: 'center',
  },
  appBar: {
    position: 'sticky',
    top: 0,    
    zIndex: 1,
  },
  formDesc: {
    fontFamily: 'Nunito',
    textAlign: 'center',
    backgroundColor: 'rgba(0,0,0,0.3)',
    borderRadius: 37,
    minHeight: 200,
    padding: 10,
  },
  goDown: {
    fontSize: '4rem',    
  },
  createForm: {
    minHeight: '100vh',
  },
}));

export default function Create() {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);
  useEffect(() => {
    setChecked(true);
  });

  return (
    <main className={classes.info}>
      <MainBar />
      <Collapse appear in={checked} {
        ... checked ? { timeout: 2000 } : {}
      }>
        <Box mt={10} m={10}>
          <Typography className={classes.formDesc} color="textSecondary" component="p">
            This is a description
          </Typography>
        </Box>
        <Scroll to="createForm" smooth="true">
        <IconButton>
          <ExpandMoreIcon className={classes.goDown} />
        </IconButton>
        </Scroll>
      </Collapse>
      <Container id="createForm" className={classes.createForm}>
      </Container>
    </main>
  );
}
