import React, { useState } from 'react';
import AppDrawer from '../Navigation/AppDrawer';
import { useFirebase } from '../Providers/FirebaseProvider';
import {
  AppBar,
  Toolbar,
  Avatar,
  IconButton,
  Typography,  
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  },
  appBar: {
    flexGrow: 1,
    background: 'transparent',
    boxShadow: 'none',
  },
  appBarWrapper: {
    width: "80%",
    margin: "0 auto"
  },
  icon: {
    color: '#fff',
    fontSize: '2rem',
  },
  header: {
    flexGrow: "1",
    fontFamily: "Nunito",
    color: '#fff'
  },
  colorText: {
    color: theme.palette.secondary.main,    
  },
}));

export default function MainBar({ color, noLogo, isAuth }) {
  const classes = useStyles();
  const firebase = useFirebase();
  const [isOpen, setIsOpen] = useState(false);

  const handleDrawer = () => {
    setIsOpen(!isOpen)
  }

  return (
    <div className={classes.root}>
      <AppBar 
        position="sticky" 
        className={classes.appBar} 
        elevation={0}
        color='transparent'
      >
        <Toolbar className={classes.appBarWrapper}>
          <Typography className={classes.header} component="h1" variant="h3">
            Thirds<span className={classes.colorText}>Media</span>
          </Typography>
          {
            firebase.user ? 
              <div>
                <AppDrawer isOpen={isOpen} handleDrawer={handleDrawer} />
                <IconButton onClick={handleDrawer}>
                  <Avatar className={classes.icon} />
                </IconButton>
              </div>
            : false
          }
        </Toolbar>
      </AppBar>
    </div>
  );
}
