import React from 'react';
import {
  Container,
  TextField,
  Grid,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    borderRadius: 17,
    fontFamily: 'Nunito',
    textAlign: 'center',
  },
  textInput: {
//    width: 550,
  },
}));

export default function BasicInfo() {
  const classes = useStyles();

  return (
    <Container maxWidth="lg" className={classes.formContainer}>
      <Grid container spacing={1}>
        <Grid item xs="6">
        <TextField 
          id='name'
          label='Name'
          variant='outlined'
          margin='normal'
          fullWidth
          InputProps={{
            className: classes.textInput
          }}
        />
        <TextField 
          id='creator'
          label='Creator'
          variant='outlined'
          margin='normal'
          fullWidth
          InputProps={{
            className: classes.textInput
          }}
        />
        </Grid>
        <Grid item xs="6">
        <TextField 
          id='location'
          label='Location'
          variant='outlined'
          margin='normal'
          fullWidth
          InputProps={{
            className: classes.textInput
          }}
        />
        <TextField 
          id='date'
          variant='outlined'
          margin='normal'
          fullWidth
          type='date'
          InputProps={{
            className: classes.textInput
          }}
        />
        </Grid>
      </Grid>
      <TextField 
        id='description'
        label='Description'
        variant='outlined'
        margin='normal'
        fullWidth
        multiline
        rows='6'
        rowsMax='10'
      />
    </Container>
  );
}
