import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import {
  Typography,
  TextField,
  Button,
  IconButton,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import AddBoxIcon from '@material-ui/icons/AddBox';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import RemoveIcon from '@material-ui/icons/Remove';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
    textAlign: 'center',
  },
  submitButton: {
    height: theme.spacing(7),
    margin: theme.spacing(2),    
  },
}));

export default function Miscellaneous() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const miscArray = [];
  const [misc, setMisc] = useState({});
  const [inputs, setInputs] = useState([{}]);
  const currentInput = inputs.length
  console.log(currentInput);

  const addNewInput = () => {
    setInputs([...inputs, {}])
  }

  const removeInput = (id) => {
    const index = inputs.indexOf(id);
    console.log("The index of field to remove: ", id)
  }

  const onChangeInput = (e) => {
    setMisc({...misc, [e.target.name]: e.target.value});
  }

  const onSubmitForm = (event) => {
    event.preventDefault()

  }

  return (
    <form className={classes.formContainer} onSubmit={onSubmitForm}>
      <Typography>
        Are there any dry or miscellaneous ingredients?
      </Typography>
      {
        inputs.map((input, id) => {
          return (
            <div>
              {
                currentInput >= 1 ?
                <IconButton
                  id={id}
                  className={classes.submitButton}
                  type="submit"
                  color="primary"
                  variant="contained"
                  onClick={(e) => removeInput(id)}
                >
                  <RemoveIcon fontSize="large"/>
                </IconButton>
                : false
              }
              <TextField 
                key={`ingredient-${id}`}
                name='name'
                label='Ingredient'
                variant='outlined'
                margin='normal'
                onChange={(e) => onChangeInput(e)}
              />
              {
                currentInput === id+1 ?
                <IconButton
                  className={classes.submitButton}
                  type="submit"
                  color="primary"
                  variant="contained"
                  onClick={addNewInput}
                //  onClick={onAddIngredientsToArray}
                >
                  <AddBoxIcon fontSize="large"/>
                </IconButton>
                : false
              }
            </div>
          );
        })
      }
      <div className={classes.buttonDiv}>
        <Button
          className={classes.nextButton}
          type="submit"
          color="primary"
          variant="contained"
          endIcon={<ExpandMoreIcon />}
          onClick={cocktail.handleNext}
        >
        { currentInput <= 1 ? 'Skip' : 'Next' }
        </Button>
      </div>
    </form>
  );
}
