import React, { useState } from 'react';
import MainBar from '../Components/MainBar';
import { useLocation } from 'react-router-dom';
import Copyright from '../Components/Copyright';
import { useFirebase } from '../Providers/FirebaseProvider';
import { useForm } from 'react-hook-form';
import {
  Container,
  Avatar,
  Typography,
  Grid,
  TextField,
  Button,
  Link,
  CircularProgress,
  Paper,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import EmailIcon from '@material-ui/icons/Email';

const useStyles = makeStyles(theme => ({
  main: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: theme.spacing(3),
  },
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: `url(${process.env.PUBLIC_URL + '/assets/polynesiancocktail.webp'})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    borderRadius: 37,
  },
  error: {
    color: theme.palette.secondary.main,
    fontFamily: 'Nunito',
    margin: theme.spacing(2),
  },
  textField: {
    borderRadius: 37,
  },
  submitButton: {
    color: 'white',
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(2),
    fontWeight: 'bold',
    borderRadius: 50,
    backgroundColor: theme.palette.primary.main,
    '&:hover': {
      backgroundColor: theme.palette.primary.hover,
    },
  },
  error: {
    color: theme.palette.secondary.main,
    fontFamily: 'Nunito',
    margin: theme.spacing(2),
  },
}))

function UserForm({ formProps }) {
  const classes = useStyles();
  const { register, errors, userData, setUserData } = formProps;

  const onChangeInput = (e) => {
    setUserData({...userData, [e.target.name]: e.target.value});
  }

  return (
    <div>
      <TextField
        label="Name"
        {...register("name", { required: true })}
        value={userData.name}
        margin="normal"
        fullWidth
        variant="outlined"
        onChange={(e) => onChangeInput(e)}
        InputProps={{className: classes.textField}}
      />
      {errors.name && <span className={classes.error}>This field is required</span>}
      <TextField
        label="Email"
        {...register("email", { required: true })}
        value={userData.email}
        margin="normal"
        fullWidth
        variant="outlined"
        onChange={(e) => onChangeInput(e)}
        InputProps={{className: classes.textField}}
      />
      {errors.email && <span className={classes.error}>This field is required</span>}
      <TextField
        label="Phone Number (optional)"
        {...register("phone")}
        value={userData.phone}
        margin="normal"
        fullWidth
        type="tel"
        variant="outlined"
        onChange={(e) => onChangeInput(e)}
        InputProps={{className: classes.textField}}
      />
      <TextField
        label="Password"
        {...register("password", { required: true })}
        value={userData.password}
        margin="normal"
        type="password"
        variant="outlined"
        fullWidth
        onChange={(e) => onChangeInput(e)}
        InputProps={{className: classes.textField}}
      />
      {errors.password && <span className={classes.error}>This field is required</span>}
    </div>
  );
}

function BusinessForm({ formProps }) {
  const classes = useStyles();
  const { register, errors, userData, setUserData } = formProps;

  const onChangeInput = (e) => {
    setUserData({...userData, [e.target.name]: e.target.value});
  }

  return (
    <Grid container component="main" className={classes.root}>
      <Grid item xs={false} sm={4} md={7} className={classes.image}>
        <MainBar />
      </Grid>
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <div className={classes.paper}>
          <Avatar className={classes.avatar}>
            <EmailIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Get In Touch 
          </Typography>
          <form 
            className={classes.form} 
            noValidate
            onSubmit={onSubmitForm}
          >
            {
              error
                ? <Typography className={classes.error}>{error}</Typography>
                : false // navigate to the success page
            }
            <Grid container spacing={2}>
              <Grid item xs>
                <TextField
                  label="Name"
                  {...register("name", { required: true })}
                  value={userData.name}
                  margin="normal"
                  fullWidth
                  variant="outlined"
                  onChange={(e) => onChangeInput(e)}
                  InputProps={{className: classes.textField}}
                />
                {errors.name && <span className={classes.error}>This field is required</span>}
              </Grid>
              <Grid item xs>
                <TextField
                  label="Company"
                  {...register("company", { required: true })}
                  value={userData.company}
                  margin="normal"
                  fullWidth
                  variant="outlined"
                  onChange={(e) => onChangeInput(e)}
                  InputProps={{className: classes.textField}}
                />
                {errors.company && <span className={classes.error}>This field is required</span>}
              </Grid>
            </Grid>
            <TextField
              label="Email"
              {...register("email", { required: true })}
              value={userData.email}
              margin="normal"
              fullWidth
              variant="outlined"
              onChange={(e) => onChangeInput(e)}
              InputProps={{className: classes.textField}}
            />
            {errors.email && <span className={classes.error}>This field is required</span>}
            <TextField
              label="Phone Number"
              {...register("phone", { required: true })}
              value={userData.phone}
              margin="normal"
              fullWidth
              type="tel"
              variant="outlined"
              onChange={(e) => onChangeInput(e)}
              InputProps={{className: classes.textField}}
            />
            {errors.phone && <span className={classes.error}>This field is required</span>}
            <TextField
              label="Address"
              {...register("address", { required: true })}
              value={userData.address}
              margin="normal"
              fullWidth
              variant="outlined"
              onChange={(e) => onChangeInput(e)}
              InputProps={{className: classes.textField}}
            />
            {errors.address && <span className={classes.error}>This field is required</span>}
            <TextField
              label="Password"
              {...register("password", { required: true })}
              value={userData.password}
              margin="normal"
              type="password"
              variant="outlined"
              fullWidth
              onChange={(e) => onChangeInput(e)}
              InputProps={{className: classes.textField}}
            />
            {errors.password && <span className={classes.error}>This field is required</span>}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Submit
            </Button>
            <Box mt={5}>
              <Copyright />
            </Box>
          </form>
        </div>
      </Grid>
    </Grid>
  );
}

