import React, { useState } from 'react';
import {
  Container,
  Typography,
  TextField,
  Button,
  Stepper,
  Step,
  StepLabel,
  StepContent,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    marginTop: theme.spacing(6),
    textAlign: 'center',
  },
  textField: {
    borderRadius: 37,
  },
  submitButton: {
    color: 'white',
    marginTop: theme.spacing(1),
    fontWeight: 'bold',
    borderRadius: 50,
    backgroundColor: theme.palette.primary.main,
    '&:hover': {
      backgroundColor: theme.palette.primary.main,
    },
  },
}))

const steps = ["Enter your email", "Reset password", "Enter code"];

function renderStep(step) {
  switch(step) {
    case 0:
      return <EnterEmail />
    case 1:
      return <TextCode />
    case 2:
      return <Verify />
    default:
      return false
  }
}

function EnterEmail() {
  const classes = useStyles();

  return (
    <Container className={classes.root} component="main" maxWidth="sm">
      <Typography component="h1" variant="h5">
        Find your account
      </Typography>
      <Typography variant="subtitle2" color="textSecondary">
        Enter the email associated with your account.
      </Typography>
      <TextField
        id="email"
        label="Email"
        margin="normal"
        required
        fullWidth
        variant="outlined"
        InputProps={{
          className: classes.textField
        }}
      />
      <Button 
        className={classes.submitButton}
        type="submit"
        color="primary"
        fullWidth
        variant="contained"
        href="/"
      >
        Search
      </Button>
    </Container>
  );
}

function TextCode() {
  const classes = useStyles();

  return (
    <Container className={classes.root} component="main" maxWidth="sm">
      <Typography component="h1" variant="h5">
        Find your account
      </Typography>
      <Typography variant="subtitle2" color="textSecondary">
        Enter the email associated with your account.
      </Typography>
      <TextField
        id="email"
        label="Email"
        margin="normal"
        required
        fullWidth
        variant="outlined"
        InputProps={{
          className: classes.textField
        }}
      />
      <Button 
        className={classes.submitButton}
        type="submit"
        color="primary"
        fullWidth
        variant="contained"
        href="/"
      >
        Search
      </Button>
    </Container>
  );
}

function Verify() {
  const classes = useStyles();

  return (
    <Container className={classes.root} component="main" maxWidth="sm">
      <Typography component="h1" variant="h5">
        Find your account
      </Typography>
      <Typography variant="subtitle2" color="textSecondary">
        Enter the email associated with your account.
      </Typography>
      <TextField
        id="email"
        label="Email"
        margin="normal"
        required
        fullWidth
        variant="outlined"
        InputProps={{
          className: classes.textField
        }}
      />
      <Button 
        className={classes.submitButton}
        type="submit"
        color="primary"
        fullWidth
        variant="contained"
        href="/"
      >
        Search
      </Button>
    </Container>
  );
}

export default function ForgotPassword() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = useState(0);

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1)
  }

  const handleNext = () => {
    if (activeStep < steps.length) {
      setActiveStep((prevActiveStep) => prevActiveStep + 1)
    } 
  }

  return (
    <Stepper orientation="vertical">
      {
        steps.map((label, index) => {
          <Step key={label} activeStep={activeStep}>
            <StepLabel>{label}</StepLabel>
            <StepContent>
              <Typography>{renderStep(index)}</Typography>
            </StepContent>
          </Step>
        })
      }
    </Stepper>
  );
}
