import React, { useState, useEffect } from 'react';
import MainBar from '../MainBar';
import BasicInfo from './BasicInfo';
import {
  Typography,
  Collapse,
  IconButton,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  Container,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import InfoOutlinedIcon from '@material-ui/icons/InfoOutlined';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  stepNav: {
    backgroundColor: '#303030',
    position: 'sticky',
    top: 0,
    zIndex: 1,
  },
  infoButton: {
    float: 'right',
  },
  helpContainer: {
    backgroundColor: 'red',
    display: 'flex',
    minHeight: '80vh',
    position: 'fixed',
    borderRadius: 37,
  },
}));

function getStepContent(step) {
  switch (step) {
    case 0:
      return <BasicInfo />
    case 1:
      return <BasicInfo />
    case 2:
      return <BasicInfo />
    default:
      return 'Unknown step';
  }
}

function Help() {
  const classes = useStyles();

  return (
    <Container maxWidth="lg" className={classes.helpContainer}>
      <Typography>
        This is help text
      </Typography>
    </Container>
  );
}

export default function Create() {
  const classes = useStyles();
  const steps = ['Basic Information', 'Base Spirit', 'Ingredients', 'Instructions'];
  const [checked, setChecked] = useState(false);
  const [activeStep, setActiveStep] = useState(0);
  const [showHelp, setShowHelp] = useState(false);

  useEffect(() => {
    setChecked(true);
  }, []);

  const handleHelp = () => {
    setHelpChecked(true);
    setShowHelp(!showHelp)
  }

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1)
  }

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1)
  }

  return (
    <main>
      <MainBar />
      <Collapse appear in={checked} {
        ... checked ? { timeout: 1000 } : {}
      }>
        <div className={classes.stepNav}>
          <IconButton
            disabled={activeStep === 0}
            onClick={handleBack}
          >
            <ExpandLessIcon />
          </IconButton>
          <IconButton
            variant="contained"
            color="primary"
            onClick={handleNext}
          >
            {
              activeStep === steps.length - 1
              ? <CheckCircleIcon /> 
              : <ExpandMoreIcon />
            }
          </IconButton>
          <IconButton className={classes.infoButton} onClick={handleHelp}>
            <InfoOutlinedIcon />
          </IconButton>
        </div>
      {
        showHelp
          ? <Help checked={helpChecked}/>
          : false
      }
        <Stepper activeStep={activeStep} orientation="vertical">
          {
            steps.map((label, index) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
                <StepContent>
                  <Typography>{getStepContent(index)}</Typography>
                </StepContent>
              </Step>
            ))
          }
        </Stepper>
      </Collapse>
    </main>
  );
}
