import React from 'react';
import { useAuth } from '../../FirebaseAuthProvider';
import {
  Container,
  Avatar,
  IconButton,
  Button,
  TextField,
  Switch,
  FormControlLabel,
  Grid,
} from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import SaveIcon from '@material-ui/icons/Save';
import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    padding: 25,

  },
  container: {
    display: 'flex',
    justifyContent: 'center',
  },
  avatar: {
    height: theme.spacing(26),
    width: theme.spacing(26),
  },
  field: {
    marginBottom: 15,
    backgroundColor: theme.palette.primary.background
  },
  switch: {
    margin: 20
  },
  button: {
    borderRadius: 37
  },
}));

export default function EditProfile() {
  const classes = useStyles();
  const auth = useAuth();

  return (
    <div className={classes.root}>
        <form className={classes.form}>
          <TextField 
            id="fname" 
            label="First Name"
            variant="outlined"
            defaultValue={auth.user.displayName} 
            className={classes.field}
            fullWidth
          />
          <TextField 
            id="bio" 
            label="Bio"
            variant="outlined"
            defaultValue={auth.user.bio} 
            className={classes.field}
            fullWidth 
            multiline 
            rows={5}
          />
          <TextField 
            id="website" 
            label="Website"
            variant="outlined"
            defaultValue={auth.user.url} 
            className={classes.field}
            fullWidth
          />
          <TextField 
            id="twitter"
            label="Twitter"
            variant="outlined"
            defaultValue={auth.user.twitter} 
            className={classes.field}
            fullWidth
          />
          <TextField 
            id="instagram"
            label="Instagram"
            variant="outlined"
            defaultValue={auth.user.instagram} 
            className={classes.field}
            fullWidth
          />
        </form>
        <Grid container justify="center">
          <Grid item xs="2">
            <Button 
              color="primary" 
              variant="outlined"
              className={classes.button}
              startIcon={<SaveIcon />}
              href="/profile"
            >
              Update Profile
            </Button>
          </Grid>
          <Grid item xs="2">
            <Button 
              color="secondary" 
              variant="outlined"
              className={classes.button}
              startIcon={<DeleteIcon />}
              href="/profile"
            >
              Cancel
            </Button>
          </Grid>
        </Grid>
    </div>
  );
}
