import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import {
  TextField,
  IconButton,
  FormControl,
  Select,
  ListSubheader,
  MenuItem,
  Button,
  Typography,
  Slider,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import { makeStyles } from '@material-ui/core/styles';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import AddBoxIcon from '@material-ui/icons/AddBox';

const useStyles = makeStyles(theme => ({
  formContainer: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  input: {
    width: 900,
  },
  formControl: {
    margin: theme.spacing(2),
    minWidth: 120,
  },
  nextItem: {
    display: 'flex',
    alignItems: 'right',
    justifyContent: 'flex-end',
  },
  buttonDiv: {
    textAlign: 'center',
  },
  nextButton: {
    borderRadius: 37,
    textTransform: 'none',
    margin: 10,
    fontFamily: 'Nunito',
  },
}));

const spirits = [
  "Bourbon",
  "Whiskey",
  "Scotch",
  "Brandy",
  "Cognac",
  "Rum",
  "Tequila",
  "Gin",
  "Vodka",
  "Absinthe",
  "Vermouth",
];

function IngredientInput({ ingredientNumber, newInput }) {
  const classes = useStyles();
  const [isUnitIngredient, setIsUnitIngredient] = useState(true);
  const [unitType, setUnitType] = useState('imperial');
  const [spiritObject, setSpiritObject] = useState({});

  const onChangeSlider = name => (event, newValue) => {
    setSpiritObject({...spiritObject, [name]: newValue})
  }

  const onChangeInput = (e) => {
    setSpiritObject({...spiritObject, [e.target.name]: e.target.value})
  }

  const handleIsUnitIngredient = () => {
    setIsUnitIngredient(!isUnitIngredient)
  }

  const handleUnitType = (e) => {
    setUnitType(e.target.value)
    setSpiritObject({...spiritObject, [e.target.name]: e.target.value})
  }

  console.log("From IngredientInput: ", ingredientNumber, newInput)
  console.log(spiritObject)

  return (
    <div>
      <div>
        {/* This is the unit type selector */}
        <FormControl variant="outlined" className={classes.formControl}>
          <Select 
            defaultValue="Spirit" 
            id="ingredientUnit"
            name="unit"
            value={unitType}
            onChange={handleUnitType}
          >
            <MenuItem value="imperial">Imperial</MenuItem>
            <MenuItem value="metric">Metric</MenuItem>
            <MenuItem value="unit">Unit</MenuItem>
          </Select>
        </FormControl>
        {/* This is where you enter the spirit name */}
        <TextField 
          id='ingredient'
          label='Ingredient'
          name='name'
          variant='outlined'
          margin='normal'
          InputProps={{className: classes.input}}
          onChange={(e) => onChangeInput(e)}
        />
        {
          // Select the spirit type here 
          unitType === "imperial" || unitType === "metric"
            ? <FormControl variant="outlined" className={classes.formControl}>
                <Select 
                  defaultValue="" 
                  name='type' 
                  id="spiritType"
                  onChange={(e) => onChangeInput(e)}
                >
                  <ListSubheader>Spirit Type</ListSubheader>
                  {
                    spirits.map((spirit, id) => {
                      return <MenuItem key={id} value={spirit}>{spirit}</MenuItem>
                    })
                  }
                </Select>
              </FormControl>
            : false
        }
      </div>
      {
        // This is the slider
        isUnitIngredient 
          ? (
            unitType === "metric"
              ? (
                <div>
                  <Typography id="discrete-slider" gutterBottom>
                    Milliliters
                  </Typography>
                  <Slider
                    id="baseSpiritAmount"
                    name="metricAmount"
                    defaultValue={15}
                    valueLabelDisplay="auto"
                    step={5}
                    min={5}
                    max={60}
                    marks
                    onChange={onChangeSlider("amount")}
                  />
                </div>
              ) : unitType === "imperial"
                  ? (
                    <div>
                      <Typography id="discrete-slider" gutterBottom>
                        Ounces
                      </Typography>
                      <Slider
                        id='baseSpiritAmount'
                        name='amount'
                        defaultValue={1}
                        valueLabelDisplay="auto"
                        step={0.25}
                        min={0.25}
                        max={6}
                        marks
                        onChange={onChangeSlider("amount")}
                      />
                    </div>
                  ) : false
          ) : false
      }
      <Button
//        className={classes.nextButton}
        type="submit"
        color="primary"
        variant="contained"
      //  onClick={cocktail.handleNext}
      >
        Submit
      </Button>
    </div>
  );
}

export default function Ingredients() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const newInput = {}
  const [inputs, setInputs] = useState([{...newInput}]);

  const addNewInput = () => {
    setInputs([...inputs, {...newInput}])
  }

  return (
    <div className={classes.formContainer}>
      <div className={classes.nextItem}>
        <IconButton onClick={addNewInput} color="primary">
          <AddBoxIcon fontSize="large"/>
        </IconButton>
      </div>
      {
        inputs.map((input, id) => { 
          return (
            <IngredientInput 
              key={`ingredient-${id}`} 
              ingredientNumber={id} 
              newInput={input} 
              setInput={setInputs} 
            />
          )
        })
      }
      <div className={classes.buttonDiv}>
        <Scroll to={`step-${cocktail.activeStep}`} smooth="true">
          <Button
            className={classes.nextButton}
            type="submit"
            color="primary"
            variant="contained"
            endIcon={<ExpandMoreIcon />}
            onClick={cocktail.handleNext}
          >
            Keep going
          </Button>
        </Scroll>
      </div>
    </div>
  );
}
