import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import { useForm } from 'react-hook-form';
import {
  FormControl,
  Typography,
  FormControlLabel,
  RadioGroup,
  Radio,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  formContainer: {
    display: 'flex',
    justifyContent: 'center',
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
}));

export default function UnitType() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const { register, handleSubmit } = useForm();
  const [unitType, setUnitType] = useState('imperial');

  const handleUnitType = (e) => {
    setUnitType(e.target.value)
  }

  const onSubmitForm = (event) => {
    event.preventDefault()

  }

  return (
      <form 
        noValidate 
        className={classes.formContainer}
        onSubmit={handleSubmit((data) => onSubmitForm(data))}
      >
        <FormControl component='fieldset'>
          <Typography>What unit of measurement will this cocktail be built with?
          </Typography>
          <RadioGroup name='unit' onChange={handleUnitType}>
            <FormControlLabel {...register('unit')} value='imperial' control={<Radio />} label='Imperial' />
            <FormControlLabel {...register('unit')} value='metric' control={<Radio />} label='Metric' />
          </RadioGroup>
        </FormControl>
      </form>
  );
}
