import React, { useState } from 'react';
import MainBar from '../Components/MainBar';
import {
  Container,
  Typography,
  TextField,
  IconButton,
  Button,
  Link,
  Grid,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  FormControl,
  FormControlLabel,
  Radio,
  RadioGroup,
} from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    marginTop: theme.spacing(6),
    textAlign: 'center'
  },
  textField: {
    borderRadius: 37,
  },
  submitButton: {
    color: 'white',
    marginTop: theme.spacing(1),
    fontWeight: 'bold',
    borderRadius: 50,
    backgroundColor: theme.palette.primary.main,
    '&:hover': {
      backgroundColor: theme.palette.primary.main,
    },
  },
}))

export default function ForgotPassword() {
  const classes = useStyles();
  const steps = ["Enter email", "Send verification", "Enter code"];
  const [activeStep, setActiveStep] = useState(0);
  const [email, setEmail] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(false);
  const [verifyMethod, setVerifyMethod] = useState('email');
  const [verified, setVerified] = useState(false);

  const handleNext = () => {
    if (activeStep < steps.length) {
      setActiveStep((prevActiveStep) => prevActiveStep + 1)
    } 
  }

  const handleVerifyMethod = (event) => {
    setVerifyMethod(event.target.value)
  }

  const searchForUserEmail = () => {
    // perform lookup in database, if found then do these next two things..
    setIsEmailValid(true)
    handleNext()
  }

  const handleSetVerify = () => {
    setVerified(true)
    handleNext()
  }

  return (
    <main>
      <MainBar />
      <Stepper activeStep={activeStep} orientation="vertical">
        <Step>
          <StepLabel>Enter your email</StepLabel>
          <StepContent>
            <Container className={classes.root} component="main" maxWidth="sm">
              <Typography variant="subtitle2" color="textSecondary">
                Enter the email associated with your account
              </Typography>
              <TextField
                id="email"
                label="Email"
                margin="normal"
                required
                fullWidth
                variant="outlined"
                onInput={ e => setEmail(e.target.value)}
                InputProps={{
                  className: classes.textField
                }}
              />
              <IconButton 
                className={classes.submitButton}
                type="submit"
                color="primary"
                fullWidth
                disabled={ !email ? true : false }
                variant="contained"
                onClick={searchForUserEmail}
              >
                <ExpandMoreIcon />
              </IconButton>
            </Container>
          </StepContent>
        </Step>
        <Step>
          <StepLabel>Send verification code</StepLabel>
          <StepContent>
            <Container maxWidth="sm">
              <Grid justify='center' alignItems='center' container spacing='3'>
                <Grid item>
                  <Typography variant="subtitle2" color="textSecondary">
                    How would you like to receive your code?
                  </Typography>
                </Grid>
                <Grid item>
                  <FormControl component="fieldset">
                    <RadioGroup aria-label="method" name="method" value={verifyMethod} onChange={handleVerifyMethod}>
                      <FormControlLabel value="phone" control={<Radio />} label="Phone number" />
                      <FormControlLabel value="email" control={<Radio />} label="Email" />
                    </RadioGroup>
                  </FormControl>
                </Grid>
              </Grid>
              <IconButton 
                className={classes.submitButton}
                type="submit"
                color="primary"
                fullWidth
                variant="contained"
                onClick={handleNext}
              >
                <ExpandMoreIcon />
              </IconButton>
            </Container>
          </StepContent>
        </Step>
        <Step>
          <StepLabel>Verify</StepLabel>
          <StepContent>
            <Container className={classes.root} component="main" maxWidth="sm">
              <Typography variant="subtitle2" color="textSecondary">
                Enter the code you received
              </Typography>
              <TextField
                id="code"
                label="Code"
                margin="normal"
                required
                fullWidth
                variant="outlined"
                InputProps={{
                  className: classes.textField
                }}
              />
              <Button 
                className={classes.submitButton}
                type="submit"
                color="primary"
                fullWidth
                variant="contained"
                onClick={handleSetVerify}
              >
                Submit
              </Button>
            </Container>
          </StepContent>
        </Step>
      </Stepper>
      {
        verified
          ? <Typography>
              Your password has been reset. Click <Link href="/login">here</Link> to login.
            </Typography>
          : false
      }
    </main>
  );
}
