import React, { useState } from 'react';
import { useFirebase } from '../Providers/FirebaseProvider';
import MainBar from '../Navigation/MainBar';
import { 
  Avatar,
  IconButton,
  Container,
  TextField,
  Grid,
  Button,
} from '@material-ui/core';
import SaveIcon from '@material-ui/icons/Save';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  field: {
    marginBottom: 15,
    backgroundColor: theme.palette.primary.background
  },
  container: {
    padding: theme.spacing(3),
  },
  button: {
    borderRadius: 37
  },
  avatar: {
    textAlign: 'center', 
    marginBottom: theme.spacing(3),
  },
  profilePic: {
    height: theme.spacing(26),
    width: theme.spacing(26),
    cursor: "pointer",
  },
}));

export default function Settings() {
  const classes = useStyles(); 
  const firebase = useFirebase();
  const [userData, setUserData] = useState({...firebase.user});
  const [image, setImage] = useState(firebase.user.picture);
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);

  const handleImageChange = (event) => {
    setFile(event.target.files[0]);
    const imageFile = URL.createObjectURL(event.target.files[0]);
    setImage(imageFile);
  }

  // Success... ish. This works, but it feels gross and dirty. 
  const onUploadImage = (event) => {
    event.preventDefault();

    if (file.size > 2097152) {
      setError("This file is too large");
    } else {
    firebase.uploadImageToStorage(file).then((url) => {
      //setImage(url); <- don't think I need that there now..
      firebase.updateUser({picture: url});
    })
    }
  }

  const onChangeInput = (event) => {
    setUserData({...userData, [event.target.name]: event.target.value})
  }

// Yep, just add all the stuff in onUploadImage into here too
  const onSubmitHandler = () => {
    firebase.updateUser(userData)
      .then(() => console.log("Successfully updated the user's data"))
      .catch((e) => console.log(e.message))
      .finally(() => window.location.reload(true))
  }

  return (
    <div>
      <MainBar />
      <Container className={classes.container}>
      <div className={classes.avatar}>
      <input 
        accept="image/*" 
        hidden 
        id="photo-upload" 
        type="file" 
        onChange={(e) => handleImageChange(e)} 
      />
      <label htmlFor="photo-upload">
        <IconButton component="span">
          <Avatar 
            className={classes.profilePic} 
            src={image}
          />
        </IconButton>
        {error ? <span>{error}</span> : false}
      </label>
      </div>
        <form>
          <TextField 
            id="displayName"
            name="displayName"
            label="Name"
            variant="outlined"
            defaultValue={userData.name}
            className={classes.field}
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
          <TextField 
            id="bio" 
            name="bio"
            label="Bio"
            variant="outlined"
            defaultValue={userData.bio}
            className={classes.field}
            fullWidth 
            multiline 
            rows={5}
            onChange={(e) => onChangeInput(e)}
          />
          <TextField 
            id="website" 
            name="website"
            label="Website"
            variant="outlined"
            defaultValue={userData.social.website}
            className={classes.field}
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
          <TextField 
            id="twitter"
            name="twitter"
            label="Twitter"
            variant="outlined"
            defaultValue={userData.social.twitter}
            className={classes.field}
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
          <TextField 
            id="instagram"
            name="instagram"
            label="Instagram"
            variant="outlined"
            defaultValue={userData.social.instagram}
            className={classes.field}
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
        </form>
        <Grid container justify="center">
          <Grid item xs={2}>
            <Button 
              color="primary" 
              variant="outlined"
              className={classes.button}
              startIcon={<SaveIcon />}
              onClick={onSubmitHandler}
            >
              Update Profile
            </Button>
          </Grid>
          <Grid item xs={2}>
            <Button 
              color="secondary" 
              variant="outlined"
              className={classes.button}
              startIcon={<DeleteIcon />}
              href="/profile"
            >
              Cancel
            </Button>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}
