import React, { useState } from 'react';
import AppDrawer from '../Navigation/AppDrawer';
import { useFirebase } from '../Providers/FirebaseProvider';
import {
  AppBar,
  Toolbar,
  Avatar,
  IconButton,
  Button,
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText,
} from '@material-ui/core';
import SortIcon from '@material-ui/icons/Sort';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import SendIcon from '@material-ui/icons/Send';
import { withStyles, makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    flexGrow: 1,
    background: 'transparent',
    boxShadow: 'none',
  },
  button: {
    fontFamily: 'Nunito',
    '&:hover': {
      color: theme.palette.secondary.main,
      backgroundColor: 'transparent',
    },
  },
  loginButton: {
    color: 'white',
    textTransform: 'none',
    backgroundColor: theme.palette.primary.main,    
    fontFamily: 'Nunito',
    '&:hover': {
      backgroundColor: theme.palette.secondary.main,
    },
  },
}));

const StyledMenu = withStyles({
  paper: {
    border: '1px solid #d3d4d5',
  },
})((props) => (
  <Menu
    elevation={0}
    getContentAnchorEl={null}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'center',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'center',
    }}
    {...props}
  />
));

const StyledMenuItem = withStyles((theme) => ({
  root: {
    '&:focus': {
      backgroundColor: theme.palette.primary.main,
      '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
        color: theme.palette.common.white,
      },
    },
  },
}))(MenuItem);

export default function MainBar() {
  const [isOpen, setIsOpen] = useState(false);
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const onOpenDrawer = () => setIsOpen(!isOpen)

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <AppBar 
        position="sticky" 
        elevation={0}
        color='transparent'
      >
        <Toolbar>
          <IconButton href="/" edge="start"> 
            <Avatar src={'../assets/ThirdsMediaSmall.png'} />
          </IconButton>
          <IconButton onClick={onOpenDrawer}>
            { firebase.user ? <SortIcon /> : false }
          </IconButton>
          <div style={{flexGrow: 1}}></div>
          { 
            firebase.user ? 
              <div>
                <AppDrawer isOpen={isOpen} handleDrawer={onOpenDrawer} />
                <IconButton onClick={handleMenu}>
                  <Avatar /> 
                </IconButton>
                <StyledMenu
                  id="customized-menu"
                  anchorEl={anchorEl}
                  keepMounted
                  open={Boolean(anchorEl)}
                  onClose={handleClose}
                >
                  <StyledMenuItem>
                    <ListItemIcon>
                      <SendIcon fontSize="small" />
                    </ListItemIcon>
                    <ListItemText primary="Profile" />
                  </StyledMenuItem>
                  <StyledMenuItem>
                    <ListItemIcon>
                      <DraftsIcon fontSize="small" />
                    </ListItemIcon>
                    <ListItemText primary="Settings" />
                  </StyledMenuItem>
                  <Divider />
                  <StyledMenuItem>
                    <ListItemIcon>
                      <InboxIcon fontSize="small" />
                    </ListItemIcon>
                    <ListItemText primary="Inbox" />
                  </StyledMenuItem>
                </StyledMenu>
              </div>
            : <Toolbar>
                <Button className={classes.button} href="/about">About</Button>
                <Button className={classes.button} href="/contact">Contact Us</Button>
                <Button className={classes.button}>Training</Button>
                <Button className={classes.loginButton} href="/signin" variant="contained">Login</Button>
              </Toolbar>
          }
        </Toolbar>
      </AppBar>
    </div>
  );
}
