import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import AppDrawer from '../Navigation/AppDrawer';
import ThirdsMediaLogo from './ThirdsMediaLogo';
import { useFirebase } from '../Providers/FirebaseProvider';
import {
  TextField,
  Grid,
  Typography,
  AppBar,
  Toolbar,
  Avatar,
  IconButton,
  Button,
  Menu,
  Link,
  MenuItem,
  ListItemIcon,
  ListItemText,
  Divider,
  InputBase,
  FormControl,
  InputLabel,
  Select,
  ListSubheader,
  InputAdornment
} from '@material-ui/core';
import SortIcon from '@material-ui/icons/Sort';
import PersonIcon from '@material-ui/icons/Person';
import SearchIcon from '@material-ui/icons/Search';
import SettingsIcon from '@material-ui/icons/Settings';
import { fade, withStyles, makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(1),
      width: 'auto',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
  formControl: {
//    margin: theme.spacing(1),
    minWidth: 120,
  },
  menu: {
    textTransform: 'none',
    fontFamily: 'Nunito',
  },
  menuGrid: {
    padding: 15,
    textAlign: 'left',
  },
}));

const StyledMenu = withStyles({
  paper: {
    border: '1px solid #d3d4d5',
  },
})((props) => (
  <Menu
    elevation={0}
    getContentAnchorEl={null}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'center',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'center',
    }}
    {...props}
  />
));

const StyledMenuItem = withStyles((theme) => ({
  root: {
    '&:focus': {
      backgroundColor: theme.palette.primary.main,
      '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
        color: theme.palette.common.white,
      },
    },
  },
}))(MenuItem);

function UserMenu() {
  const classes = useStyles();
  const firebase = useFirebase();
  const history = useHistory();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <IconButton onClick={handleMenu}>
        <Avatar /> 
      </IconButton>
      <StyledMenu
        id="customized-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <Grid container className={classes.menuGrid}>
          <Grid item>
            <IconButton>
              <Avatar />
            </IconButton>
          </Grid>
          <Grid item>
            <Typography variant="h6" className={classes.menu}>
              {firebase.user.name}
            </Typography>
          </Grid>
        </Grid>
        <Link href="/profile" color="inherit">
          <StyledMenuItem>
            <ListItemIcon>
              <PersonIcon fontSize="small" />
            </ListItemIcon>
            <ListItemText primary="Profile" />

          </StyledMenuItem>
        </Link>
        <Link href="/editprofile" color="inherit">
          <StyledMenuItem>
            <ListItemIcon>
              <SettingsIcon fontSize="small" />
            </ListItemIcon>
            <ListItemText primary="Settings" />
          </StyledMenuItem>
        </Link>
        <Divider />
        <div style={{margin: 10, textAlign: 'center'}}>
          <Button 
            className={classes.menu} 
            color="secondary" 
            variant="contained"
            onClick={() => {
              firebase.signout()
                .catch((e) => console.log(e.message))                
                .finally(() => history.push("/signin"));
            }}
          >
            Log out
          </Button>
        </div>
      </StyledMenu>
    </div>
  );
}

function SearchInput() {
  const classes = useStyles();

  const spirits = [
    "Bourbon",
    "Whiskey",
    "Scotch",
    "Brandy",
    "Cognac",
    "Rum",
    "Tequila",
    "Gin",
    "Vodka",
    "Absinthe",
    "Vermouth",
  ];

  return (
    <div>
      <div className={classes.search}>
        <div className={classes.searchIcon}>
          <SearchIcon />
        </div>
        <InputBase
          placeholder="Search…"
          classes={{
            root: classes.inputRoot,
            input: classes.inputInput,
          }}
          inputProps={{ 'aria-label': 'search' }}
          endAdornment={
            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="search-filter">Filter by</InputLabel>
              <Select defaultValue="" id="search-filter">
                <MenuItem value={'name'}>Cocktail Name</MenuItem>
                <MenuItem value={'creator'}>Cocktail Creator</MenuItem>
                <ListSubheader>Base Spirit'</ListSubheader>
                {
                  spirits.map((spirit, id) => {
                    return <MenuItem value={spirit}>{spirit}</MenuItem>
                  })
                }
              </Select>
            </FormControl>
          }
        />
      </div>
    </div>
  );
}

export default function MainBar() {
  const [isOpen, setIsOpen] = useState(false);

  const onOpenDrawer = () => setIsOpen(!isOpen)

  return (
    <AppBar 
      position="sticky" 
      color="inherit"
    >
      <Toolbar>
        <ThirdsMediaLogo />
        <IconButton onClick={onOpenDrawer}>
          <SortIcon />
        </IconButton>
        <div style={{flexGrow: 1}}></div>
        <SearchInput />

        <AppDrawer isOpen={isOpen} handleDrawer={onOpenDrawer} />
        <UserMenu /> 
      </Toolbar>
    </AppBar>
  );
}
