import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import {
  Typography,
  TextField,
  Button,
  IconButton,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import AddBoxIcon from '@material-ui/icons/AddBox';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
    textAlign: 'center',
  },
  submitButton: {
    margin: theme.spacing(2),    
  },
}));

export default function Miscellaneous() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const miscArray = [];
  const [misc, setMisc] = useState({});
  const [inputs, setInputs] = useState([{}]);

  const addNewInput = () => setInputs([...inputs, {}])

  const onChangeInput = (e) => {
    setMisc({...misc, [e.target.name]: e.target.value});
  }

  const onSubmitForm = (event) => {
    event.preventDefault()

  }

  return (
    <form className={classes.formContainer} onSubmit={onSubmitForm}>
      <Typography>
        Are there any dry or miscellaneous ingredients?
      </Typography>
      <IconButton onClick={addNewInput} color="primary">
        <AddBoxIcon fontSize="large"/>
      </IconButton>
      {
        inputs.map((input, id) => {
          return (
            <div>
              <TextField 
                key={`ingredient-${id}`}
                name='name'
                label='Ingredient'
                variant='outlined'
                margin='normal'
                onChange={(e) => onChangeInput(e)}
              />
              <Button
                className={classes.submitButton}
                type="submit"
                color="primary"
                variant="contained"
              //  onClick={onAddIngredientsToArray}
              >
                Submit
              </Button>
            </div>
          );
        })
      }
      <div className={classes.buttonDiv}>
        <Button
          className={classes.nextButton}
          type="submit"
          color="primary"
          variant="contained"
          endIcon={<ExpandMoreIcon />}
          onClick={cocktail.handleNext}
        >
          Next
        </Button>
      </div>
    </form>
  );
}
