import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import AppDrawer from '../Navigation/AppDrawer';
import ThirdsMediaLogo from './ThirdsMediaLogo';
import { useFirebase } from '../Providers/FirebaseProvider';
import {
  TextField,
  Grid,
  Typography,
  AppBar,
  Toolbar,
  Avatar,
  IconButton,
  Button,
  Menu,
  Link,
  MenuItem,
  ListItemIcon,
  ListItemText,
  Divider,
  InputBase,
  FormControl,
  InputLabel,
  Select,
  ListSubheader,
  InputAdornment
} from '@material-ui/core';
import SortIcon from '@material-ui/icons/Sort';
import PersonIcon from '@material-ui/icons/Person';
import SearchIcon from '@material-ui/icons/Search';
import SettingsIcon from '@material-ui/icons/Settings';
import { fade, withStyles, makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  searchBox: {
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
    margin: 10,
  },
  menu: {
    textTransform: 'none',
    fontFamily: 'Nunito',
  },
  menuGrid: {
    padding: 15,
    textAlign: 'left',
  },
}));

const StyledMenu = withStyles({
  paper: {
    border: '1px solid #d3d4d5',
  },
})((props) => (
  <Menu
    elevation={0}
    getContentAnchorEl={null}
    anchorOrigin={{
      vertical: 'bottom',
      horizontal: 'center',
    }}
    transformOrigin={{
      vertical: 'top',
      horizontal: 'center',
    }}
    {...props}
  />
));

const StyledMenuItem = withStyles((theme) => ({
  root: {
    '&:focus': {
      backgroundColor: theme.palette.primary.main,
      '& .MuiListItemIcon-root, & .MuiListItemText-primary': {
        color: theme.palette.common.white,
      },
    },
  },
}))(MenuItem);

function UserMenu() {
  const classes = useStyles();
  const firebase = useFirebase();
  const history = useHistory();
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <IconButton onClick={handleMenu}>
        <Avatar /> 
      </IconButton>
      <StyledMenu
        id="customized-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <Grid container className={classes.menuGrid}>
          <Grid item>
            <IconButton>
              <Avatar />
            </IconButton>
          </Grid>
          <Grid item>
            <Typography variant="h6" className={classes.menu}>
              {firebase.user.name}
            </Typography>
          </Grid>
        </Grid>
        <Link href="/profile" color="inherit">
          <StyledMenuItem>
            <ListItemIcon>
              <PersonIcon fontSize="small" />
            </ListItemIcon>
            <ListItemText primary="Profile" />

          </StyledMenuItem>
        </Link>
        <Link href="/editprofile" color="inherit">
          <StyledMenuItem>
            <ListItemIcon>
              <SettingsIcon fontSize="small" />
            </ListItemIcon>
            <ListItemText primary="Settings" />
          </StyledMenuItem>
        </Link>
        <Divider />
        <div style={{margin: 10, textAlign: 'center'}}>
          <Button 
            className={classes.menu} 
            color="secondary" 
            variant="contained"
            onClick={() => {
              firebase.signout()
                .catch((e) => console.log(e.message))                
                .finally(() => history.push("/signin"));
            }}
          >
            Log out
          </Button>
        </div>
      </StyledMenu>
    </div>
  );
}

function SearchInput() {
  const classes = useStyles();

  const spirits = [
    "Bourbon",
    "Whiskey",
    "Scotch",
    "Brandy",
    "Cognac",
    "Rum",
    "Tequila",
    "Gin",
    "Vodka",
    "Absinthe",
    "Vermouth",
  ];

  // This needs to stylistic repairing
  return (
    <div>
    <TextField 
              id="search"
              variant="outlined"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />              
                  </InputAdornment>
                ),
                className: classes.searchBox
              }}
            />
    {/*
      <FormControl>
        <InputLabel htmlFor="search-filter">Filter by</InputLabel>
        <Select defaultValue="" id="search-filter">
          <MenuItem value={'name'}>Cocktail Name</MenuItem>
          <MenuItem value={'creator'}>Cocktail Creator</MenuItem>
          <ListSubheader>Base Spirit'</ListSubheader>
          {
            spirits.map((spirit, id) => {
              return <MenuItem value={spirit}>{spirit}</MenuItem>
            })
          }
        </Select>
      </FormControl>
      */
    }
    </div>
  );
}

export default function MainBar() {
  const [isOpen, setIsOpen] = useState(false);

  const onOpenDrawer = () => setIsOpen(!isOpen)

  return (
    <AppBar 
      position="sticky" 
      color="inherit"
    >
      <Toolbar>
        <ThirdsMediaLogo />
        <IconButton onClick={onOpenDrawer}>
          <SortIcon />
        </IconButton>
        <div style={{flexGrow: 1}}></div>
        <SearchInput />

        <AppDrawer isOpen={isOpen} handleDrawer={onOpenDrawer} />
        <UserMenu /> 
      </Toolbar>
    </AppBar>
  );
}
