import React, { useState } from 'react';
import MetricSelector from '../Helpers/MetricSelector';
import ImperialSelector from '../Helpers/ImperialSelector';
import {
  Container,
  TextField,
  Grid,
  FormControlLabel,
  Checkbox,
  IconButton,
  Button,
  Switch,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import AddBoxIcon from '@material-ui/icons/AddBox';
const useStyles = makeStyles(theme => ({
  formContainer: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  input: {
    width: 900,
  },
  formControl: {
    margin: theme.spacing(2),
    minWidth: 120,
  },
  nextButton: {
    width: 250,
    marginTop: 20,
    borderRadius: 37,
  },
}));

function IngredientInput({ isMetric }) {
  const classes = useStyles();

  return (
    <div>
    <Grid container alignItems="center">
      <Grid item xs>
        <TextField 
          id='ingredient'
          label='Ingredient'
          variant='outlined'
          margin='normal'
          InputProps={{
            className: classes.input
          }}
        />
      </Grid>
      <Grid item xl>
        <IconButton color="primary">
          <AddBoxIcon fontSize="large"/>
        </IconButton>
      </Grid>
    </Grid>
    {
      isMetric
        ? <MetricSelector />
        : <ImperialSelector />
    }

          <FormControlLabel
            control={
              <Switch
                name="recipe-type"
                color="secondary"
                onChange={handleIsRecipe}
              />
            }
            label={switchLabel}
          />
    </div>
  );
}

export default function Ingredients() {
  const classes = useStyles();
  const [isMetric, setIsMetric] = useState(false);
  const [isRecipe, setIsRecipe] = useState(false);
  const switchLabel = isRecipe ? "Cocktail" : "Recipe" 

  const handleIsMetric = () => {
    setIsMetric(!isMetric)
  }

  const handleIsRecipe = () => {
    setIsRecipe(!isRecipe)
  }

  return (
    <Container maxWidth="lg" className={classes.formContainer}>
      <Grid container>
        <Grid item>
          <FormControlLabel
            control={<Checkbox color="secondary" id="metric" size="small" />}
            label="Metric"
            onChange={handleIsMetric}
          />
        </Grid>
        <Grid item>

        </Grid>
      </Grid>
      <IngredientInput isMetric={isMetric} />
      <Button
        variant="outlined"
        color="primary"
        className={classes.nextButton}
      >
        Next
      </Button>
    </Container>
  );
}
