import React from 'react';
import helpText from '../static/helpText';
import {
  Box,
  Typography
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  helpContainer: {
    backgroundColor: '#404040',
    zIndex: 1,
    minHeight: '40vh',
    position: 'fixed',
    border: 'solid white 1px',
    boxShadow: 1,
    borderRadius: 37,
  },
  helpText: {
    fontFamily: 'Nunito',
    textAlign: 'center',
    padding: 10,
    margin: 10,
  },
}));

export default function Help({ step }) {
  const classes = useStyles();

  const steps = () => {
    switch (step) {
      case 0:
        return helpText[0]
      case 1:
        return helpText[1]
      case 2:
        return helpText[2]
      case 3:
        return helpText[3]
      default:
        return false
    }
  }

  return (
    <div>
    {
      step <= 3
      ? <Box className={classes.helpContainer}>
          <Typography 
            component="p" 
            className={classes.helpText} 
            color="textSecondary"
          >
            { steps() }
          </Typography>
        </Box>
      : false
    }
    </div>
  );
}
