import React from 'react';
import MainBar from './MainBar';
import ProfilePic from './assets/dj-pct.jpg';

import {
	Box,
	Grid,
	Avatar,
	Typography,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
	account: {
		display: 'flex',
		flexDirection: 'space-between', // I suck
		margin: 25,
		padding: 20,
		borderRadius: 25,
		backgroundColor: theme.palette.secondary.main,
	},
	mainText: {
		fontFamily: 'Nunito',
	},
	settings: {
		diplay: 'flex',
		flexDirection: 'column',
	},
	profilePic: {
		height: theme.spacing(26),
		width: theme.spacing(26),
	},
}));

export default function Profile({ profileData }) {
	const classes = useStyles();

	return (
		<div>
			<Box className={classes.account}>
				<Avatar className={classes.profilePic} src={ProfilePic} />
				<Typography component="h1" variant="h3" className={classes.mainText}>
					{profileData.name}
				</Typography>
			</Box>
			<Grid container className={classes.settings}>
				<Grid item>
				Name: 
				</Grid>
				<Grid item>
				Occuption:
				</Grid>
				<Grid item>
				Employer:
				</Grid>
			</Grid>
		</div>
	);
}
