import React from 'react';
import MainBar from '../Components/MainBar';
import {
  Container,
  Grid,
  Link,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Typography,
} from '@material-ui/core';
import cocktailImage from '../assets/cocktail.jpg';
import { makeStyles } from '@material-ui/core/styles';

import exampleDatabase from '../static/exampleDatabase';

const useStyles = makeStyles(theme => ({
  root: {
    flex: 'center',
    justifyContent: 'center',
  },
  card: {
    background: 'rgba(0,0,0,0.3)',
    textAlign: 'center',
    borderRadius: 10, 
    minWidth: 320,
    maxWidth: 345,
  },
  cardMedia: {
    height: 210,
  },
  content: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
  },
  text: {
    fontFamily: 'Nunito',
  },
}));

export default function CardList() {
  const classes = useStyles();

  return (
    <div>
    <MainBar />
    <Container>
      <Grid container spacing={4} className={classes.root}>
      {
        exampleDatabase.map((card) => {
          return (
            <Grid key={card.id} item>
              <Link href="/cocktail">
                <Card raised className={classes.card}>
                  <CardActionArea>
                    <CardMedia
                      className={classes.cardMedia}
                      image={cocktailImage}
                      title="MoonBoots"
                    />
                    <CardContent>
                      <Typography 
                        gutterBottom 
                        variant="h5" 
                        component="h2"
                        className={classes.text}
                      >
                        {card.name}
                      </Typography>
                      <Grid container className={classes.content} justify='center'>
                        {
                          card.ingredients.map((item) => {
                            return (
                              <Typography 
                                key={item.id}
                                variant="body2" 
                                color="textSecondary" 
                                component="p" 
                              >
                                {item.name+", "}
                              </Typography>
                            );
                          })
                        }
                      </Grid>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Link>
            </Grid>
          )
        })
      }
      </Grid>
    </Container>
    </div>
  );
}
