import React from 'react';
import MainBar from './MainBar';
import QRCode from './QRCode';
import {
	Avatar,
	Container,
  Grid,
	Typography,
	Button,
} from '@material-ui/core';
import LinkIcon from '@material-ui/icons/Link';
import ProfilePic from './assets/dj-pct.jpg';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
	container: {
    display: 'flex',
    padding: 20,
		backgroundColor: '#202020', // ok so that color looks way better anyway
	},
  navContainer: {
		display: 'flex',
		justifyContent: 'center',
	},
	info: {
		display: 'flex',
		flexDirection: 'column',
    fontFamily: 'Nunito',
    margin: 25,
		backgroundColor: '#303030', // this is just for testing purposes
		borderRadius: 15,
	},
  title: {
    fontFamily: 'Nunito' ,
  },
  bio: {
    fontFamily: 'Nunito',
    color: '#d3d3d3',
  },
	profileText: {
		color: theme.palette.primary.main,
		fontFamily: 'Nunito',
		marginBottom: 25,
	},
	editButton: {
		borderRadius: 50,
		height: 37,
		width: 160,
		fontFamily: 'Nunito',
		textTransform: 'none',
	},
  navButton: {
    fontFamily: 'Nunito',
    textTransform: 'none',
  },
  linkButton: {
    fontFamily: 'Nunito',
    textTransform: 'none',
  },
	profilePic: {
		height: theme.spacing(26),
		width: theme.spacing(26),
	},
}));

export default function Profile({ profileData }) {
	const classes = useStyles();

	return (
    <Container component="main" maxWidth="xl">
      {/* profile container */}
			<div className={classes.container}>
        <Avatar className={classes.profilePic} src={ProfilePic} />
				<Container className={classes.info}>
          <Typography component="h1" variant="h3" className={classes.title}>
            {profileData.name}
          </Typography>
          <Grid container>
            <Grid item xs="1">
              {profileData.occupation}
            </Grid>
            <Grid item xs="1">
              <Button className={classes.linkButton}>
                <LinkIcon /> {' '}{profileData.url}
              </Button>
            </Grid>
          </Grid>
          <Typography className={classes.bio}>
            {profileData.bio}
          </Typography>
				</Container>
				<Button variant="outlined" color="primary" className={classes.editButton}>
					Edit Profile
				</Button>
      </div>

      {/* Nav buttons */}
      <Grid container >
        <Grid item xs="1">
          <Button color="primary" className={classes.navButton}>Other stuff</Button>
        </Grid>
        <Grid item xs="1">
          <Button color="primary" className={classes.navButton}>Favorites</Button>
        </Grid>
        <Grid item xs="1">
          <Button color="primary" className={classes.navButton}>QR Code</Button>
        </Grid>
      </Grid>
      <Container className={classes.navContainer}>
				<QRCode />
			</Container>
    </Container>
	);
}
