import React, { useState } from 'react';
import { useFirebase } from '../Providers/FirebaseProvider';
import { useCocktail } from '../Providers/CocktailProvider';
import MainBar from '../Navigation/MainBar';
import { 
  Avatar,
  IconButton,
  TextField,
  Grid,
  Button,
} from '@material-ui/core';
import SaveIcon from '@material-ui/icons/Save';
import DeleteIcon from '@material-ui/icons/Delete';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  field: {
    marginBottom: 15,
    backgroundColor: theme.palette.primary.background
  },
  container: {
    padding: theme.spacing(3),
    textAlign: 'center',
  },
  button: {
    borderRadius: 37
  },
  avatar: {
    textAlign: 'center', 
    marginBottom: theme.spacing(3),
  },
  profilePic: {
    height: theme.spacing(26),
    width: theme.spacing(26),
    cursor: "pointer",
  },
}));

export default function EditCocktail() {
  const classes = useStyles(); 
  const firebase = useFirebase();
  const cocktail = useCocktail();
  const [error, setError] = useState(null);

  const handleImageChange = (event) => {
    setFile(event.target.files[0]);
    const imageFile = URL.createObjectURL(event.target.files[0]);
    setImage(imageFile);
  }

  const onChangeInput = (event) => {
    setUserData({...userData, [event.target.name]: event.target.value})
  }

  const onSubmitHandler = (e) => {
    e.preventDefault();
  }

  return (
    <div>
      <MainBar />
      <form className={classes.container} onSubmit={onSubmitHandler}>
        <input 
          accept="image/*" 
          hidden 
          id="photo-upload" 
          type="file" 
          onChange={(e) => handleImageChange(e)} 
        />
        <label htmlFor="photo-upload">
          <IconButton component="span">
            <Avatar 
              className={classes.profilePic} 
              src={image}
            />
          </IconButton>
          {error ? <span>{error}</span> : false}
        </label>
      <Grid container spacing={1}>
        <Grid item xs>
          <TextField 
            id='name'
            name='name'
            defaultValue={cocktail.recipe.name}
            label='Cocktail Name'
            variant='outlined'
            margin='normal'
            fullWidth
          />
          <TextField 
            id='creator'
            name='creator'
            defaultValue={cocktail.recipe.creator}
            label='Creator'
            variant='outlined'
            margin='normal'
            fullWidth
          />
        </Grid>
        <Grid item xs>
          <TextField 
            id='location'
            name='location'
            defaultValue={cocktail.recipe.location}
            label='Location'
            variant='outlined'
            margin='normal'
            fullWidth
          />
          <TextField 
            id='date'
            name='data'
            defaultValue={cocktail.recipe.date}
            variant='outlined'
            margin='normal'
            fullWidth
            type='date'
          />
        </Grid>
      </Grid>
      <TextField 
        id='description'
        name='description'
        defaultValue={cocktail.recipe.description}
        label='Description'
        variant='outlined'
        margin='normal'
        fullWidth
        multiline
        rows='6'
        rowsMax='10'
      />
        {
          cocktail.recipe.ingredients.map((ingredient, id) => { 
            return (
              <Grid container className={classes.ingredientsList}>
                <Grid item xs={2}>
                  <RemoveButton onClick={() => onRemoveIngredient(ingredient.id)} />
                </Grid>
                <Grid item xs>
                  <Typography key={id}>
                    Name: {ingredient.name}
                  </Typography>
                </Grid>
                <Grid item xs>
                  <Typography>
                    Amount: {ingredient.amount}
                  </Typography>
                </Grid>
              <Divider variant='fullWidth' className={classes.divider} />
              </Grid>
            );
          })
        }
        <Grid container justify="center">
          <Grid item xs={2}>
            <Button 
              color="primary" 
              variant="outlined"
              type="submit"
              className={classes.button}
              startIcon={<SaveIcon />}
            >
              Update Cocktail
            </Button>
          </Grid>
          <Grid item xs={2}>
            <Button 
              color="secondary" 
              variant="outlined"
              className={classes.button}
              startIcon={<DeleteIcon />}
              href="/profile"
            >
              Cancel
            </Button>
          </Grid>
        </Grid>
      </form>
    </div>
  );
}
