import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import { useForm } from 'react-hook-form';
import {
  FormControl,
  FormLabel,
  FormControlLabel,
  RadioGroup,
  Radio,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
}));

export default function UnitType() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const { register, handleSubmit, watch, formState: { errors } } = useForm();
  const [unitType, setUnitType] = useState('imperial');

  const handleUnitType = (e) => {
    setUnitType(e.target.value)
  }


  const onSubmitForm = (event) => {
    event.preventDefault()

  }

  return (
      <form 
        noValidate 
        className={classes.formContainer}
        onSubmit={onSubmitForm}
      >
        <FormControl component='fieldset' className={classes.formControl}>
          <FormLabel component='legend'>Unit Type</FormLabel>
          <RadioGroup row aria-label='unitType' name='unit' onChange={handleUnitType}>
            <FormControlLabel {...register('unit')} value='imperial' control={<Radio />} label='Imperial' />
            <FormControlLabel {...register('unit')} value='metric' control={<Radio />} label='Metric' />
          </RadioGroup>
        </FormControl>
      </form>
  );
}
