import React from 'react';
import MainBar from './MainBar';
import QRCode from './QRCode';
import {
	Grid,
	Avatar,
	Container,
	Typography,
	Button,
} from '@material-ui/core';
import ProfilePic from './assets/dj-pct.jpg';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
	container: {
		display: 'flex',
		padding: 20,
		width: '100%',
		borderRadius: 25,
		backgroundColor: theme.palette.secondary.main,
	},
	profileMain: {
		display: 'flex',
		flexDirection: 'column',
	},
	info: {
		display: 'flex',
		flexDirection: 'column',
		fontFamily: 'Nunito',
		margin: 25
	},
	bio: {
		color: theme.palette.primary.main,
	},
	editProfile: {
		display: 'flex',
		flexDirection: 'column',
		fontFamily: 'Nunito',
	},
	profilePic: {
		height: theme.spacing(26),
		width: theme.spacing(26),
	},
}));

function EditProfile({ profileData }) {
	const classes = useStyles();

	// This should be a different component that gets navigated to by pushing a button in Profile
	return (
		<Container>
			<Grid container className={classes.editProfile}>
				<Grid item>
					Name: {profileData.name}
				</Grid>
				<Grid item>
					Occuption: {profileData.occupation}
				</Grid>
				<Grid item>
					Employer: {profileData.employer}
				</Grid>
				<Grid item className={classes.bio}>
					{profileData.bio}
				</Grid>
			</Grid>
		</Container>
	);
}

export default function Profile({ profileData }) {
	const classes = useStyles();

	return (
		<main>
			<div className={classes.container}>
				<Avatar className={classes.profilePic} src={ProfilePic} />
				<Container className={classes.profileMain}>
					<Typography component="h1" variant="h3" className={classes.info}>
						{profileData.name}
					</Typography>
				</Container>

			</div>
			<QRCode />
		</main>
	);
}
