import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import { useForm } from 'react-hook-form';
import {
  Grid,
  TextField,
  IconButton,
  FormControl,  
  Select,
  ListSubheader,
  MenuItem,
  Button,
  Typography,
  Slider,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import AddBoxIcon from '@material-ui/icons/AddBox';

const useStyles = makeStyles(theme => ({
  formContainer: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  input: {
    width: 900,
  },
  formControl: {
    margin: theme.spacing(2),
    minWidth: 120,
  },
  nextItem: {
    display: 'flex',
    alignItems: 'right',
    justifyContent: 'flex-end',
  },
  buttonDiv: {
    textAlign: 'center',
  },
  submitButton: {
    height: theme.spacing(7),
    margin: theme.spacing(2),
  },
  nextButton: {
    borderRadius: 37,
    textTransform: 'none',
    margin: 10,
    fontFamily: 'Nunito',
  },
}));

const types = [
  "baseSpirit",
  "modifier",
  "nonAlcoholic",
  "misc",
];

export default function Ingredients() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const { register, handleSubmit } = useForm();
  const ingredients = [{}];
  const [spiritObject, setSpiritObject] = useState({});
  const [inputs, setInputs] = useState([{
    amount: '',
    name: '',
    type: '',
    unit: unitType,
  }]);

  const onSubmit = (data) => {
    alert(JSON.stringify(data))
  }

  const onChangeSlider = name => (event, newValue) => {
    setSpiritObject({...spiritObject, [name]: newValue})
  }

  const onChangeInput = (e) => {
    setSpiritObject({...spiritObject, [e.target.name]: e.target.value})
  }

  const onAddIngredientsToArray = () => {
    ingredients.push(spiritObject)
  }

  return (
    <form 
      noValidate 
      className={classes.formContainer}
      id='ingredients'
      name='ingredients'
      value={ingredients}
      onSubmit={handleSubmit((data) => onSubmit(data))} 
    >
      <Grid container alignItems='center'>
        <Grid item>
          <IconButton onClick={() => setInputs([...inputs, {}])} color="primary">
            <AddBoxIcon fontSize="large"/>
          </IconButton>
        </Grid>
      </Grid>
      {
        inputs.map((input, id) => { 
          return (
            <div>
              <TextField 
                {...register(`ingredient${id}`)}
                id='ingredient'
                label='Ingredient'
                variant='outlined'
                margin='normal'
                InputProps={{className: classes.input}}
                onChange={(e) => onChangeInput(e)}
              />
              <FormControl variant="outlined" className={classes.formControl}>
                <Select 
                  {...register('type')}
                  defaultValue="" 
                  name='type' 
                  id="spiritType"
                  onChange={(e) => onChangeInput(e)}
                >
                  <ListSubheader>Ingredient Type</ListSubheader>
                  {
                    types.map((type, id) => {
                      return <MenuItem key={id} value={type}>{type}</MenuItem>
                    })
                  }
                </Select>
              </FormControl>
              <Button
                className={classes.submitButton}
                color="primary"
                variant="contained"
                onClick={onAddIngredientsToArray}
              >
                Submit
              </Button>
              {
                cocktail.recipe.unit === "metric" ? 
                  <div>
                    <Typography id="discrete-slider" gutterBottom>
                      Milliliters
                    </Typography>
                    <Slider
                      {...register('amount')}
                      id="baseSpiritAmount"
                      name="metricAmount"
                      defaultValue={15}
                      valueLabelDisplay="auto"
                      step={5}
                      min={5}
                      max={60}
                      marks
                      onChange={onChangeSlider("amount")}
                    />
                  </div>
                : <div>
                    <Typography id="discrete-slider" gutterBottom>
                      Ounces
                    </Typography>
                    <Slider
                      {...register('amount')}
                      id='baseSpiritAmount'
                      name='amount'
                      defaultValue={1}
                      valueLabelDisplay="auto"
                      step={0.25}
                      min={0.25}
                      max={6}
                      marks
                      onChange={onChangeSlider("amount")}
                    />
                  </div>
              }
            </div>
          )
        })
      }
      <div className={classes.buttonDiv}>
        <Button
          className={classes.nextButton}
          type="submit"
          color="primary"
          variant="outlined"
          endIcon={<ExpandMoreIcon />}
          onClick={cocktail.handleNext}
        >
          Keep going
        </Button>
      </div>
    </form>
  );
}
