import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import { useForm, Controller } from 'react-hook-form';
import {
  Grid,
  TextField,
  IconButton,
  FormControl,  
  Select,
  ListSubheader,
  MenuItem,
  Button,
  Typography,
  Slider,
  Divider,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { v4 as uuidv4 } from 'uuid';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import AddBoxIcon from '@material-ui/icons/AddBox';
import RemoveIcon from '@material-ui/icons/Remove';

const useStyles = makeStyles(theme => ({
  formContainer: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  input: {
    width: 900,
  },
  grid: {
    gridGap: theme.spacing(5),
    textAlign: 'center',
    justifyContent: 'center',
  },
  inputField: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    margin: 10,
  },
  slider: {
    marginTop: 20,
  },
  formControl: {
    display: 'flex',
    flexDirection: 'row',
    margin: theme.spacing(2),
    minWidth: 120,
  },
  nextItem: {
    display: 'flex',
    alignItems: 'right',
    justifyContent: 'flex-end',
  },
  divider: {
    margin: 25,
  },
  buttonDiv: {
    textAlign: 'center',
  },
  nextButton: {
    borderRadius: 37,
    textTransform: 'none',
    margin: 10,
    fontFamily: 'Nunito',
  },
}));

const units = [
  "fl. oz",
  "ml",
  "dash",
];

const RemoveButton = ({ onClick }) => (
  <RemoveIcon
    fontSize="large"
    onClick={onClick}
    style={{ cursor: "pointer" }}
  />
);

const NextButton = ({ onClick }) => {
  <div className={classes.buttonDiv}>
    <Button
      className={classes.nextButton}
      type="submit"
      color="primary"
      variant="outlined"
      endIcon={<ExpandMoreIcon />}
      onClick={onClick}
    >
      Keep going
    </Button>
  </div>
}

const InputField = ({ id, addIngredient, onChange }) => {
  const classes = useStyles();

  return (
    <div className={classes.inputField}>
      <TextField 
        label="Ingredient Name"
        id={id} 
        name='name' 
        variant='outlined'
        onChange={onChange} 
        InputProps={{ className: classes.input}}
      />
      <IconButton>
        <AddBoxIcon
          color="primary"
          fontSize="large"
          onClick={addIngredient}
        />
      </IconButton>
    </div>
  );
};

export default function Ingredients() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const { register, handleSubmit, control } = useForm();
  const [unitType, setUnitType] = useState('fl. oz');
  const [spiritObject, setSpiritObject] = useState({});
  const [ingredients, setIngredients] = useState([]);

  const onChangeInput = (e) => {
    setSpiritObject({...spiritObject, [e.target.name]: e.target.value})
  }

  const onAddIngredient = () => {
    setIngredients([...ingredients, {id: uuidv4(), ...spiritObject}])
  }

  const onRemoveIngredient = (id) => {
    console.log(ingredients.findIndex((ingredient) => ingredient.id === id));
    setIngredients(ingredients.filter((ingredient) => ingredient.id !== id));
  }

  const onSubmit = () => {
    console.log("Ingredients: ", ingredients)
  }

  return (
    <form 
      noValidate 
      className={classes.formContainer}
      id='ingredients'
      name='ingredients'
      onSubmit={handleSubmit(onSubmit)} 
    >
      <div>
        <InputField
          onChange={(e) => onChangeInput(e)}
          addIngredient={onAddIngredient}
        />
        {
          ingredients.length > 0 ?
            <div>
              <Typography id="ozSlider" gutterBottom>
                Ounces
              </Typography>
              <Grid container className={classes.grid}>
                <Grid item xs={10}>
                  <Slider
  //                  {...register('amount')}
                    id='amount'
                    defaultValue={1}
                    valueLabelDisplay="auto"
                    step={0.25}
                    min={0.25}
                    max={6}
                    marks
                    className={classes.slider}
                  />
                </Grid>
                <Grid item>
                  <FormControl variant="outlined">
                    <Select 
   //                   {...register('type')}
                      defaultValue={unitType}
                      id="spiritType"
                      className={classes.selector}
                      onChange={(e) => onChangeInput(e)}
                    >
                      <ListSubheader>Ingredient Type</ListSubheader>
                      {
                        units.map((unit, id) => {
                          return <MenuItem key={id} value={unit}>{unit}</MenuItem>
                        })
                      }
                    </Select>
                  </FormControl>
                </Grid>
              </Grid>
              <div className={classes.buttonDiv}>
                <Button
                  className={classes.nextButton}
                  type="submit"
                  color="primary"
                  variant="outlined"
                >
                  Add Ingredient
                </Button>
              </div>
              <Divider className={classes.divider} />
              {
                ingredients.map((ingredient, id) => { 
                  return (
                    <div>
                    <RemoveButton onClick={() => onRemoveIngredient(ingredient.id)} />
                    <Typography key={id}>
                      {ingredient.name}
                    </Typography>
                    </div>
                  );
                })
              }
            </div> : false
        }
      </div>
      <NextButton onClick={cocktail.handleNext} />
    </form>
  );
}
