import React from 'react';
import {
  Container,
  Grid,
  Link,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Typography,
} from '@material-ui/core';
import cocktailImage from '../assets/cocktail.jpg';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    marginTop: theme.spacing(1),
  //  flex: 'center',
    justifyContent: 'center',
    display: 'grid',
  },
  card: {
    background: 'rgba(0,0,0,0.3)',
    textAlign: 'center',
    borderRadius: 10, 
    minWidth: 320,
    maxWidth: 345,
  },
  cardMedia: {
    height: 210,
  },
  content: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
  },
  text: {
    fontFamily: 'Nunito',
  },
}));

export default function CardList({ data }) {
  const classes = useStyles();

  return (
    <Container>
      <Grid container spacing={4} className={classes.root}>
      {
        data.map((card) => {
          return (
            <Grid key={card.id} item>
              <Link href="/cocktail">
                <Card raised className={classes.card}>
                  <CardActionArea>
                    <CardMedia
                      className={classes.cardMedia}
                      image={cocktailImage}
                      title="MoonBoots"
                    />
                    <CardContent>
                      <Typography 
                        gutterBottom 
                        variant="h5" 
                        component="h2"
                        className={classes.text}
                      >
                        {card.name}
                      </Typography>
                      <Grid container className={classes.content} justify='center'>
                        {
                          card.ingredients.map((item) => {
                            return (
                              <Typography 
                                key={item.id}
                                variant="body2" 
                                color="textSecondary" 
                                component="p" 
                              >
                                {item.name+", "}
                              </Typography>
                            );
                          })
                        }
                      </Grid>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Link>
            </Grid>
          )
        })
      }
      </Grid>
    </Container>
  );
}
