import React from 'react';
import helpText from '../static/helpText';
import {
  Drawer,
  Grid,
  Typography,
  Divider
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    padding: 10,
    maxWidth: 500,
  },
  title: {
    textAlign: 'center',
    fontFamily: 'Nunito',
    padding: 5,
  },
}));

export default function InfoDrawer({ step, stepTitle, isOpen, handleDrawer }) {
  const classes = useStyles();

  const steps = () => {
    switch (step) {
      case 0:
        return `help text1 aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa
                  aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaBBBBBBBBBBBBBBBBBBBBB
                  CCCCCCCCCCCCCCCCCCCCCCC`
      case 1:
        return helpText[1]
      case 2:
        return helpText[2]
      case 3:
        return helpText[3]
      default:
        return false
    }
  }

  return (
    <Drawer
      anchor='right'
      open={isOpen}
      onClose={handleDrawer}
      variant='persistent'
    >
      <Grid container direction='column' className={classes.root}>
        {
          step <= 3
          ? (
            <div>
              <Typography className={classes.title} variant='h5'>{stepTitle}</Typography>
              <Divider />
              <p>{steps()}</p>
            </div>
          )
          : false
        }
      </Grid>
    </Drawer>
  );
}
