import React, { useState } from 'react';
import { useCocktail } from '../Providers/CocktailProvider';
import {
  TextField,
  FormControl,
  Select,
  ListSubheader,
  MenuItem,
  Button,
  Typography,
  Slider,
} from '@material-ui/core';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  input: {
    width: 650
  },
  formControl: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
    minWidth: 120,
  },
  buttonDiv: {
    textAlign: 'center',
  },
  nextButton: {
    borderRadius: 37,
    textTransform: 'none',
    margin: 10,
    fontFamily: 'Nunito',
  },
}));

const spirits = [
  "Bourbon",
  "Whiskey",
  "Scotch",
  "Brandy",
  "Cognac",
  "Rum",
  "Tequila",
  "Gin",
  "Vodka",
  "Absinthe",
  "Vermouth",
];

export default function UnitType() {
  const classes = useStyles();
  const cocktail = useCocktail();
  const [unitType, setUnitType] = useState('imperial');
  const [baseSpirit, setBaseSpirit] = useState({
    amount: 0,
    name: '',
    type: '',
    unit: unitType,
  });

  const handleUnitType = (e) => {
    setUnitType(e.target.value)
    setBaseSpirit({...baseSpirit, [e.target.name]: e.target.value})
  }

  const onChangeInput = (e) => {
    setBaseSpirit({...baseSpirit, [e.target.name]: e.target.value})
  }

  const onChangeSlider = name => (event, newValue) => {
    setBaseSpirit({...baseSpirit, [name]: newValue})
  }

  const onSubmitForm = (event) => {
    event.preventDefault()

    if (baseSpirit) {
      cocktail.buildCocktailIngredient(baseSpirit)
    }
  }

  return (
      <form noValidate onSubmit={onSubmitForm}>
      </form>
  );
}
