import React from 'react';
import MainBar from './MainBar';
import QRCode from './QRCode';
import {
  AppBar,
  Avatar,
  Container,
  Link,
  Grid,
  Typography,
  Button,
  Breadcrumbs,
  Tabs,
  Tab
} from '@material-ui/core';
import LinkIcon from '@material-ui/icons/Link';
import TwitterIcon from '@material-ui/icons/Twitter';
import InstagramIcon from '@material-ui/icons/Instagram';
import ProfilePic from './assets/dj-pct.jpg';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  container: {
    display: 'flex',
    padding: 20,
    backgroundColor: '#202020', // ok so that color looks way better anyway
  },
  navContainer: {
    display: 'flex',
    justifyContent: 'center',
  },
  info: {
    display: 'flex',
    flexDirection: 'column',
    fontFamily: 'Nunito',
    borderRadius: 15,
  },
  editButton: {
    borderRadius: 50,
    height: 37,
    width: 160,
    fontFamily: 'Nunito',
    textTransform: 'none',
  },
  navButton: {
    fontFamily: 'Nunito',
    textTransform: 'none',
  },
  profilePic: {
    height: theme.spacing(26),
    width: theme.spacing(26),
  },
  breadcrumbs: {
    fontFamily: 'Nunito',
    margin: 20
  },
  link: {
    display: 'flex',
  },
}));

export default function Profile({ profileData }) {
  const classes = useStyles();

  return (
    <div>
      {/* profile container */}
      <Container maxWidth="xl" className={classes.container}>
        <Avatar className={classes.profilePic} src={ProfilePic} />
        <Container className={classes.info}>
          <Typography component="h1" variant="h3" style={{fontFamily: 'Nunito'}}>
            {profileData.name}
          </Typography>
          <span style={{color: '#d0d0d0'}}>{profileData.username}</span>
          <Breadcrumbs className={classes.breadcrumbs}>
            <Link rel="noopener" href={profileData.twitter} className={classes.link}>
              <TwitterIcon />
            </Link>
            <Link rel="noopener" href={profileData.instagram} className={classes.link}>
              <InstagramIcon />
            </Link>
            <Link rel="noopener" href={profileData.url} className={classes.link}>
              <LinkIcon />
            </Link>
          </Breadcrumbs>
          <Typography className={classes.bio} style={{fontFamily: 'Roboto'}}>
            {profileData.bio}
          </Typography>
        </Container>
        <Button variant="outlined" color="primary" className={classes.editButton}>
          Edit Profile
        </Button>
      </Container>
      <AppBar position="static">
        <Tabs value="stuff" aria-label="simple tabs example">
          <Tab label="Info" />
          <Tab label="Favorites" />
          <Tab label="QR Code" />
        </Tabs>
      </AppBar>
      <QRCode />
      {/* 
        Nav buttons 
        Need to use the Fixed Tabs component from Material UI here
      <Grid container >
        <Grid item xs="1">
          <Button color="primary" className={classes.navButton}>Other stuff</Button>
        </Grid>
        <Grid item xs="1">
          <Button color="primary" className={classes.navButton}>Favorites</Button>
        </Grid>
        <Grid item xs="1">
          <Button color="primary" className={classes.navButton}>QR Code</Button>
        </Grid>
      </Grid>
      <Container className={classes.navContainer}>
        <QRCode />
      </Container>
      */}
    </div>
  );
}
