import React, { useState, useEffect } from 'react';
import BasicInfo from './BasicInfo';
import BaseSpirit from './BaseSpirit';
import Ingredients from './Ingredients';
import Instructions from './Instructions';
import {
  Collapse,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  Typography,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  appBar: {
    backgroundColor: '#303030',
    position: 'sticky',
    top: 0,
    zIndex: 1,
  },
  infoButton: {
    float: 'right',
  },
  button: {
    borderRadius: 37,
    width: 250
  },
  buttonDiv: {
    flex: 1,
    margin: 25,
    textAlign: 'center',
  },
}));

function renderStep(step) {
  switch (step) {
    case 0:
        // this needs to be different
      return <BasicInfo />
    case 1:
      return <Ingredients />
    case 2: 
      // This also needs to be different
      return <Instructions />
    default:
      return false
  }
}

export default function RecipeStepper({ steps, activeStep }) {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);

  useEffect(() => {
    setChecked(true);
  }, []);

  return (
    <Collapse appear in={checked} {
      ... checked ? { timeout: 1000 } : {}
    }>
      <Stepper 
        id={`step-${activeStep}`} 
        activeStep={activeStep} 
        orientation="vertical"
      >
        {
          steps.map((label, index) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
              <StepContent>
                <Typography>{renderStep(index)}</Typography>
                {
                  // Stop showing the secondary ExpandMoreIcon if you've reached the Instructions step
                  // Also, this ExpandMoreIcon should push data to the recipe object array
                  /*index < steps.length - 1
                    ? <IconButton color="primary">
                        <ExpandMoreIcon onClick={handleNext} fontSize="large" />
                      </IconButton>
                    : false
                    */
                }
              </StepContent>
            </Step>
          ))
        }
      </Stepper>
    </Collapse>
  )
}
