import React, { useState } from 'react';
import AppDrawer from '../Navigation/AppDrawer';
import { useFirebase } from '../Providers/FirebaseProvider';
import {
  AppBar,
  Toolbar,
  Avatar,
  IconButton,
  Typography,  
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  },
  appBar: {
    flexGrow: 1,
    background: 'transparent',
    boxShadow: 'none',
  },
  icon: {
    color: '#fff',
    fontSize: '2rem',
  },
  header: {
    flexGrow: "1",
    fontFamily: "Nunito",
    color: '#fff'
  },
  colorText: {
    color: theme.palette.secondary.main,    
  },
}));

function LoggedInContent() {
  const classes = useStyles();
  const [isOpen, setIsOpen] = useState(false);

  const handleDrawer = () => {
    setIsOpen(!isOpen)
  }
  return (
    <div>
      <AppDrawer isOpen={isOpen} handleDrawer={handleDrawer} />
      <IconButton onClick={handleDrawer}>
        <Avatar className={classes.icon} />
      </IconButton>
    </div>
  );
}

function LoggedOutContent() {
  return (
    <div>Hi</div>
  );
}

export default function MainBar() {
  const classes = useStyles();
  const firebase = useFirebase();

  return (
    <div className={classes.root}>
      <AppBar 
        position="sticky" 
        className={classes.appBar} 
        elevation={0}
        color='transparent'
      >
        <Toolbar>
          <Typography className={classes.header} component="h1" variant="h3">
            Thirds<span className={classes.colorText}>Media</span>
          </Typography>
          { firebase.user ? <LoggedInContent /> : <LoggedOutContent /> }
        </Toolbar>
      </AppBar>
    </div>
  );
}
