import React from 'react';
import {
  Container,
  Grid,
  TextField,
  Button,
  Select,
  FormControl,
  InputLabel,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import AddAPhotoIcon from '@material-ui/icons/AddAPhoto';

const useStyles = makeStyles(theme => ({
  formContainer: {
    display: 'flex',
    flexDirection: 'column',
    textAlign: 'center',
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  button: {
    width: 250
  },
  buttonDiv: {
    paddingTop: 20,
    textAlign: 'center',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

const glassware = [
  "Rocks",
  "Collins",
  "Highball",
  "Lowball",
  "Hurricane",
  "Tiki",
  "Coupe",
  "Nick and Nora",
  "Martini",
];

const methods = [
  "Shake",
  "Stir",
  "In Glass",
  "Whip Shake",
  "Infuse",
];

export default function Instructions() {
  const classes = useStyles();

  return (
    <Container maxWidth="md" className={classes.formContainer}>
    <Grid container>
      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="age-native-simple">Build Method</InputLabel>
        <Select
          native
          inputProps={{
            name: 'age',
            id: 'age-native-simple',
          }}
        >
          <option aria-label="None" value="" />
          {
            methods.map((method) => {
              return <option value={method}>{method}</option>
            })
          }
        </Select>
      </FormControl>
      <FormControl className={classes.formControl}>
          <InputLabel htmlFor="age-native-simple">Glassware</InputLabel>
          <Select
            native
            inputProps={{
              name: 'age',
              id: 'age-native-simple',
            }}
          >
            <option aria-label="None" value="" />
            <option value={"Shake"}>Shake</option>
            <option value={"Stir"}>Stir</option>
            <option value={"Build In Glass"}>Build In Glass</option>
            <option value={"Whip Shake"}>Whip Shake</option>
            <option value={"Whip Shake"}>Infuse</option>
          </Select>
        </FormControl>
      </Grid>
      <TextField 
        id='instructions'
        label='Instructions'
        variant='outlined'
        margin='normal'
        multiline
        rows='5'
        maxRows='10'
      />
      <div className={classes.buttonDiv}>
        <Button
          className={classes.button}
          variant="outlined"
          color="secondary"
          size='small'
          startIcon={<AddAPhotoIcon />}
        >
          Choose Image
        </Button>
      </div>
    </Container>
  );
}
