import React, { useState } from 'react';
import Copyright from '../Components/Copyright';
import { useHistory } from 'react-router-dom';
import { useFirebase } from '../Providers/FirebaseProvider';
import {
  Container,
  Avatar,
  Typography,
  TextField,
  Button,
  FormControlLabel,
  Grid,
  Link,
  Checkbox,
  CircularProgress,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import LockOpenIcon from '@material-ui/icons/LockOpen';

const useStyles = makeStyles(theme => ({
  paper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: theme.spacing(6),
    border: '1px solid #d3d4d5',
    padding: theme.spacing(3),
  },
  colorText: {
    color: theme.palette.primary.main,
  },
  submitButton: {
    color: 'white',
    margin: theme.spacing(1),
    textTransform: 'none',
    fontWeight: 'bold',
  },
  logo: {
    height: theme.spacing(10),
    width: theme.spacing(10),
  },
  error: {
    color: theme.palette.secondary.main,
    fontFamily: 'Nunito',
    margin: theme.spacing(2),
  },
}));

export default function SignIn() {
  const classes = useStyles();
  const firebase = useFirebase();
  const history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(firebase.error);

  const onChangeHandler = (event) => {
    const {name, value} = event.currentTarget;

    if (name === 'userEmail') {
      setEmail(value);
    } else if (name === 'userPassword') {
      setPassword(value);
    }
  }

  const onSignInHandler = () => {
    firebase.signin().finally(() => history.push("/home"));
  }

  if (firebase.loading) {
    return (
      <div className={classes.loading}>
        <CircularProgress color="secondary" />
      </div>
    )
  }

  return (
    <div>
    <Container className={classes.paper} component="main" maxWidth="xs">
      <Typography style={{fontFamily: 'Nunito'}} component="h1" variant="h5">
        Log in to <span className={classes.colorText}>JADE</span>
      </Typography>
      {
        error
          ? <Typography className={classes.error}>{error}</Typography>
          : false
      }
      <TextField
        id="email"
        name="userEmail"
        label="Email"
        margin="normal"
        value={email}
        required
        fullWidth
        variant="outlined"
        onChange={(e) => onChangeHandler(e)}
      />
      <TextField
        id="password"
        name="userPassword"
        label="Password"
        value={password}
        margin="normal"
        type="password"
        required
        fullWidth
        variant="outlined"
        onChange={(e) => onChangeHandler(e)}
      />
      <Grid container>
        <FormControlLabel
          control={<Checkbox color="secondary" id="remember" size="small" />}
          label="Remember Me"
        />
      </Grid>
      <Button 
        className={classes.submitButton}
        type="submit"
        name="reg"
        color="primary"
        fullWidth
        variant="contained"
        onClick={onSignInHandler}
      >
        Sign In
      </Button>
      <Button 
        className={classes.submitButton}
        type="submit"
        name="goog"
        color="secondary"
        fullWidth
        variant="contained"
        onClick={() => {
          firebase.signInWithGoogle().catch((e) => {
            setError(e.message)
          })
        }}
      >
        Sign in with Google
      </Button>
      <Grid 
        container
        justify="space-around"
      >
        <Grid item>
          <Link href="/forgotpassword" variant="body2">
            Forgot password?
          </Link>
        </Grid>
        <Grid item>
          <Link href="/" variant="body2">
            Sign Up
          </Link>
        </Grid>
      </Grid>
      <Copyright />
    </Container>
    </div>
  );
}
