import React from 'react';
import {
  Container,
  Typography,
  FormControl,
  InputLabel,
  Select,
  Grid,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
}));

export default function BasicInfo() {
  const classes = useStyles();

  return (
      <Grid container justify="center" alignItems="center" className={classes.formContainer}>
        <Grid item xs>
        <Typography>
          What kind of recipe is this?
        </Typography>
        </Grid>
        <Grid item xs>
          <FormControl className={classes.formControl}>
            <InputLabel htmlFor="recipe-type">Recipe Type</InputLabel>
            <Select
              native
              inputProps={{
                name: 'recipe-type',
                id: 'recipe-type',
              }}
            >
              <option aria-label="None" value="" />
              <option value="Cocktail">Cocktail</option>
              <option value="MSI">Mix/Syrup/Infusion</option>
            </Select>
          </FormControl>
        </Grid>
      </Grid>
  );
}
