import React, { useState } from 'react';
import { useFirebase } from '../Providers/FirebaseProvider';
import {
  Container,
  Typography,
  TextField,
  Button,
  Link,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  step: {
    marginTop: theme.spacing(6),
    textAlign: 'center'
  },
  textField: {
    borderRadius: 37,
  },
  submitButton: {
    color: 'white',
    marginTop: theme.spacing(1),
    fontWeight: 'bold',
    borderRadius: 50,
    backgroundColor: theme.palette.primary.main,
    '&:hover': {
      backgroundColor: theme.palette.primary.main,
    },
  },
  message: {
    fontFamily: 'Nunito',
  },
}))

export default function ForgotPassword() {
  const classes = useStyles();
  const auth = useFirebase();
  const [email, setEmail] = useState('');
  const [error, setError] = useState(false);
  const [message, setMessage] = useState(null);

  const onResetPasswordHandler = () => {
    // perform lookup in database, if found then do these next two things..
    auth.sendPasswordResetEmail(email)
      .then(() => setMessage(`An email has been sent to ${email}`))
      .catch((err) => {
        setError(true)
        setMessage(err.message)
      })
  }

  return (
    <main>
      <Container className={classes.step} maxWidth="sm">
        <Typography variant="subtitle2" color="textSecondary">
          Enter the email associated with your account
        </Typography>
        <TextField
          id="email"
          label="Email"
          margin="normal"
          required
          fullWidth
          variant="outlined"
          onInput={ e => setEmail(e.target.value)}
          InputProps={{
            className: classes.textField
          }}
        />
        <Button 
          className={classes.submitButton}
          type="submit"
          color="primary"
          fullWidth
          variant="contained"
          onClick={onResetPasswordHandler}
        >
          Submit
        </Button>
      </Container>
      {
        message
          ? <Typography 
              color={error ? "secondary" : "primary"}
              className={classes.message} 
              align="center" 
              variant="h6"
            >
              {message}. Login<Link style={{color: 'white'}} href="/"> here</Link>
            </Typography>
          : false
      }
    </main>
  );
}
