import React from 'react';
import {
  Container,
  Typography,
  FormControl,
  InputLabel,
  Select,
  Grid,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    fontFamily: 'Nunito',
  },
  formControl: {
    margin: theme.spacing(1)
  },
}));

export default function BasicInfo() {
  const classes = useStyles();

  return (
      <Grid container justify="center" alignItems="center" className={classes.formContainer}>
        <Grid item xs>
        <Typography>
          What kind of recipe is this?
        </Typography>
        </Grid>
        <Grid item xs>
          <FormControl component="fieldset">
            <FormLabel component="legend">Recipe Type</FormLabel>
            <RadioGroup aria-label="type1" name="type1" value={value}>
              <FormControlLabel value="female" control={<Radio />} label="Female" />
              <FormControlLabel value="male" control={<Radio />} label="Male" />
              <FormControlLabel value="other" control={<Radio />} label="Other" />
              <FormControlLabel value="disabled" disabled control={<Radio />} label="(Disabled option)" />
            </RadioGroup>
          </FormControl>
        </Grid>
      </Grid>
  );
}
