import React, { useState, useEffect } from 'react';
import { useLocation, useParams } from 'react-router-dom';
import Copyright from '../Components/Copyright';
import { useFirebase } from '../Providers/FirebaseProvider';
import { useForm } from 'react-hook-form';
import {
  Container,
  Typography,
  Grid,
  TextField,
  Button,
  Link,
  CircularProgress,
  Paper,
  Box,
  Divider,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  main: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: theme.spacing(3),
  },
  root: {
    height: '100vh',
  },
  jade: {
    color: theme.palette.primary.main,
    fontWeight: 'bold',
  },
  thirds: {
    color: theme.palette.secondary.main,
  },
  text: {
    fontFamily: 'Nunito',
  },
  image: {
    backgroundImage: `url(${process.env.PUBLIC_URL + '/assets/pour.jpeg'})`,
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    margin: theme.spacing(4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  error: {
    color: theme.palette.secondary.main,
    fontFamily: 'Nunito',
    margin: theme.spacing(2),
  },
  paragraph: {
    marginTop: theme.spacing(2),
    textAlign: 'center',
    fontFamily: 'Nunito',
    color: '#d0d0d0',
  },
  submitButton: {
    color: 'white',
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(2),
    fontWeight: 'bold',
    backgroundColor: theme.palette.primary.main,
    '&:hover': {
      backgroundColor: theme.palette.primary.hover,
    },
  },
}))

function UserForm({ formProps }) {
  const classes = useStyles();
  const { register, errors, userData, setUserData } = formProps;

  const onChangeInput = (e) => {
    setUserData({...userData, [e.target.name]: e.target.value});
  }

  return (
    <div>
      <Divider />
      <Typography className={classes.paragraph}>
        Get started with JADE the one and only hospitality social media network. 
      </Typography>
      <Grid container spacing={2}>
      <TextField
        label="Name"
        {...register("name", { required: true })}
        value={userData.name}
        margin="normal"
        fullWidth
        variant="outlined"
        onChange={(e) => onChangeInput(e)}
      />
      {errors.name && <span className={classes.error}>This field is required</span>}
      <TextField
        label="Email"
        {...register("email", { required: true })}
        value={userData.email}
        margin="normal"
        fullWidth
        variant="outlined"
        onChange={(e) => onChangeInput(e)}
      />
      {errors.email && <span className={classes.error}>This field is required</span>}
      <TextField
        label="Phone Number (optional)"
        {...register("phone")}
        value={userData.phone}
        margin="normal"
        fullWidth
        type="tel"
        variant="outlined"
        onChange={(e) => onChangeInput(e)}
      />
      <TextField
        label="Password"
        {...register("password", { required: true })}
        value={userData.password}
        margin="normal"
        type="password"
        variant="outlined"
        fullWidth
        onChange={(e) => onChangeInput(e)}
      />
      </Grid>
      {errors.password && <span className={classes.error}>This field is required</span>}
    </div>
  );
}

function BusinessForm({ formProps }) {
  const classes = useStyles();
  const { register, errors, userData, setUserData } = formProps;

  // Need to set the account type as well as isAdmin on the user object
  const onChangeInput = (e) => {
    setUserData({...userData, [e.target.name]: e.target.value});
  }

  return (
    <div>
      <Divider />
      <Typography className={classes.paragraph}>
        Elevate your team and your brand to new heights with <span className={classes.thirds}>ThirdsMedia</span>. 
      </Typography>
      <Grid container spacing={2}>
        <Grid item md>
          <TextField
            label="Name"
            {...register("name", { required: true })}
            value={userData.name}
            margin="normal"
            variant="outlined"
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
          {errors.name && <span className={classes.error}>This field is required</span>}
        </Grid>
        <Grid item md>
          <TextField
            label="Company"
            {...register("company", { required: true })}
            value={userData.company}
            margin="normal"
            variant="outlined"
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
          {errors.company && <span className={classes.error}>This field is required</span>}
        </Grid>
      </Grid>
      <Grid container spacing={2}>
        <Grid item md>
          <TextField
            label="Email"
            {...register("email", { required: true })}
            value={userData.email}
            margin="normal"
            variant="outlined"
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
          {errors.email && <span className={classes.error}>This field is required</span>}
        </Grid>
        <Grid item md>
          <TextField
            label="Phone Number"
            {...register("phone", { required: true })}
            value={userData.phone}
            margin="normal"
            type="tel"
            variant="outlined"
            fullWidth
            onChange={(e) => onChangeInput(e)}
          />
        </Grid>
      </Grid>
      {errors.phone && <span className={classes.error}>This field is required</span>}
      <TextField
        label="Password"
        {...register("password", { required: true })}
        value={userData.password}
        margin="normal"
        type="password"
        variant="outlined"
        fullWidth
        Change={(e) => onChangeInput(e)}
        onChange={(e) => onChangeInput(e)}
      />
      <TextField
        label="Confirm Password"
        {...register("confirm", { required: true })}
        value={userData.confirm}
        margin="normal"
        type="password"
        variant="outlined"
        fullWidth
        Change={(e) => onChangeInput(e)}
        onChange={(e) => onChangeInput(e)}
      />
      {errors.password && <span className={classes.error}>This field is required</span>}
    </div>
  );
}

export default function SignUp() {
  const classes = useStyles();
  const firebase = useFirebase();
  const location = useLocation();
  const { type } = useParams();
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [userData, setUserData] = useState({});
  const formProps = { register, errors, userData, setUserData };

  useEffect(() => {
    if (firebase.loading) {
      return (
        <div className={classes.loading}>
          <CircularProgress color="secondary" />
        </div>
      )
    }

    setUserData({...userData, accountType: type});
  }, []);

  const onSubmitForm = () => firebase.signup(userData);

  return (
    <Grid container className={classes.root}>
      <Grid item xs={false} sm={4} md={6} className={classes.image} />
      <Grid item xs={12} sm={8} md={6} component={Paper} elevation={6} square>
        <div className={classes.paper}>
          <Typography className={classes.text} component="h1" variant="h4">
            Sign up for <span className={classes.jade}>Jade</span>
          </Typography>
          <form noValidate onSubmit={handleSubmit(onSubmitForm)}>
            {
              type === 'business' ?
                <BusinessForm formProps={formProps} />
              : <UserForm formProps={formProps} />
            }
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submitButton}
            >
              Submit
            </Button>
          </form>
