import React from 'react';
import Help from './Help';
import {
  Drawer,
} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme) => ({
  root: {
    margin: 5,
    minWidth: 200,
  },
  avatar: {
    padding: 10,
  },
  title: {
    fontFamily: 'Nunito',
  },
}));

export default function InfoDrawer({ activeStep, isOpen, handleDrawer }) {
  const classes = useStyles();

  return (
    <Drawer
      anchor='right'
      open={isOpen}
      onClose={handleDrawer}
      variant='temporary'
    >
      <div className={classes.root}>
        <Help step={activeStep} />
      </div>
    </Drawer>
  );
}
