import React, { useState, useEffect } from 'react';
import MainBar from './MainBar';
import {
  Box,
  Typography,
  Collapse,
  IconButton,
  Container,
  TextField,
} from '@material-ui/core';
import { Link as Scroll } from 'react-scroll';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
  info: {
    minHeight: '100vh',    
    textAlign: 'center',
  },
  appBar: {
    position: 'sticky',
    top: 0,    
    zIndex: 1,
  },
  formContainer: {
    backgroundColor: 'rgba(0,0,0,0.3)',
    padding: 20,
    borderRadius: 17,
    fontFamily: 'Nunito',
    marginTop: 50,
  },
  goDown: {
    fontSize: '4rem',    
  },
  textInput: {
    borderRadius: 37,
    width: 500,
  },
  createForm: {
    minHeight: '100vh',
  },
}));

export default function Create() {
  const classes = useStyles();
  const [checked, setChecked] = useState(false);
  useEffect(() => {
    setChecked(true);
  });

  return (
    <main className={classes.info}>
      <MainBar hasInfoIcon />
      <Collapse appear in={checked} {
        ... checked ? { timeout: 1000 } : {}
      }>
        <Container maxWidth="lg" className={classes.formContainer}>
          <TextField 
            id='name'
            label='Name'
            variant='outlined'
            margin='normal'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='creator'
            label='Creator'
            variant='outlined'
            margin='normal'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='location'
            label='Location'
            variant='outlined'
            margin='normal'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='date'
            variant='outlined'
            margin='normal'
            type='date'
            InputProps={{
              className: classes.textInput
            }}
          />
          <TextField 
            id='description'
            label='Description'
            variant='outlined'
            margin='normal'
            multiline
            rows='6'
            rowsMax='10'
            InputProps={{
              className: classes.textInput
            }}
          />
        </Container>
        <Scroll to="createForm" smooth="true">
          <IconButton>
            <ExpandMoreIcon className={classes.goDown} />
          </IconButton>
        </Scroll>
      </Collapse>
      <div id="createForm" className={classes.createForm}>
        {/* The build form here */}
      </div>
    </main>
  );
}
